 116	 /**
 117	 * Orchestrate parallel sync pipelines (following codebase-indexer pattern)
 118	 */
 119	 private async orchestrateParallelSync(
 120	 codebaseId: string,
 121	 type: SyncJobType,
 122	 syncJobId: string,
 123	 metadata: any,
 124	 ): Promise<void> {
 125	 this.logger.log(`Orchestrating parallel sync for codebase: ${codebaseId}, type: ${type}`);
 126	
 127	 try {
 128	 // Phase 1: Repository Sync & File Discovery
 129	 await this.updateProgress(codebaseId, syncJobId, 'repository-sync', 10, 'Starting repository sync and file discovery');
 130	 await this.executeRepositorySync(codebaseId, syncJobId, metadata);
 131	
 132	 // Phase 2: Get discovered files
 133	 await this.updateProgress(codebaseId, syncJobId, 'file-discovery', 20, 'Discovering files in repository');
 134	 const files = await this.getDiscoveredFiles(codebaseId);
 135	
 136	 if (files.length === 0) {
 137	 this.logger.warn(`No files discovered for codebase ${codebaseId}`);
 138	 await this.updateProgress(codebaseId, syncJobId, 'completed', 100, 'No files found to process');
 139	 return;
 140	 }
 141	
 142	 // Phase 3: Execute parallel processing pipelines (codebase-indexer approach)
 143	 await this.updateProgress(codebaseId, syncJobId, 'parallel-processing', 30, `Processing ${files.length} files in parallel pipelines`);
 144	 await this.executeParallelProcessingPipelines(codebaseId, files, syncJobId, type);
 145	
 146	 // Phase 4: Finalize and update codebase status
 147	 await this.updateProgress(codebaseId, syncJobId, 'finalizing', 90, 'Finalizing sync process');
 148	 await this.finalizeSyncProcess(codebaseId, syncJobId);
 149	
 150	 await this.updateProgress(codebaseId, syncJobId, 'completed', 100, 'Sync completed successfully');
 151	 } catch (error) {
 152	 this.logger.error(`Error in parallel sync orchestration for codebase ${codebaseId}:`, error);
 153	 await this.updateProgress(codebaseId, syncJobId, 'failed', 0, `Sync failed: ${error.message}`);
 154	 throw error;
 155	 }
 156	 }
 157	
 158	 /**
 159	 * Execute repository sync and file discovery
 160	 */
 161	 private async executeRepositorySync(
 162	 codebaseId: string,
 163	 syncJobId: string,
 164	 metadata: any,
 165	 ): Promise<void> {
 166	 this.logger.log(`Executing repository sync for codebase: ${codebaseId}`);
 167	
 168	 try {
 169	 // Update progress
 170	 await this.updateSyncProgress(syncJobId, codebaseId, 10, 'Syncing repository...');
 171	
 172	 // Get codebase information
 173	 const codebase = await this.prisma.codebase.findUnique({
 174	 where: { id: codebaseId },
 175	 });
 176	
 177	 if (!codebase) {
 178	 throw new Error(`Codebase not found: ${codebaseId}`);
 179	 }
 180	
 181	 // Sync repository files
 182	 const syncResult = await this.gitlabService.syncRepository(
 183	 codebase.gitlabProjectId,
 184	 codebase.branch || 'main',
 185	 );
 186	
 187	 // Update progress
 188	 await this.updateSyncProgress(syncJobId, codebaseId, 30, 'Discovering files...');
 189	
 190	 // Scan and discover files
 191	 const discoveredFiles = await this.fileScannerService.scanRepository(
 192	 syncResult.repositoryPath,
 193	 {
 194	 includePatterns: metadata?.includePatterns,
 195	 excludePatterns: metadata?.excludePatterns,
 196	 maxFileSize: metadata?.maxFileSize,
 197	 },
 198	 );
 199	
 200	 // Store discovered files
 201	 await this.fileService.storeDiscoveredFiles(codebaseId, discoveredFiles);
 202	
 203	 // Update progress
 204	 await this.updateSyncProgress(syncJobId, codebaseId, 50, `Discovered ${discoveredFiles.length} files`);
 205	
 206	 this.logger.log(`Repository sync completed for codebase: ${codebaseId}, files: ${discoveredFiles.length}`);
 207	 } catch (error) {
 208	 this.logger.error(`Repository sync failed for codebase ${codebaseId}:`, error);
 209	 throw error;
 210	 }
 211	 }
 212	
 213	 /**
 214	 * Execute parallel processing pipelines (codebase-indexer approach)
 215	 */
 216	 private async executeParallelProcessingPipelines(
 217	 codebaseId: string,
 218	 files: any[],
 219	 syncJobId: string,
 220	 type: SyncJobType,
 221	 ): Promise<void> {
 222	 this.logger.log(`Executing parallel processing pipelines for ${files.length} files`);
 223	
 224	 try {
 225	 // Update progress
 226	 await this.updateSyncProgress(syncJobId, codebaseId, 60, 'Starting parallel processing...');
 227	
 228	 // Calculate batch configuration
 229	 const batchSize = this.config?.performance?.maxFilesPerBatch || 100;
 230	 const totalBatches = Math.ceil(files.length / batchSize);
 231	
 232	 // Parallel execution (like codebase-indexer's Go routines)
 233	 const pipelinePromises: Promise<any>[] = [];
 234	
 235	 // Pipeline 1: File Processing + Embedding Generation
 236	 if (type === SyncJobType.FULL || type === SyncJobType.EMBEDDING) {
 237	 pipelinePromises.push(
 238	 this.executeFileProcessingPipeline(codebaseId, files, totalBatches, syncJobId)
 239	 );
 240	 }
 241	
 242	 // Pipeline 2: Code Analysis (SCIP + Tree-sitter)
 243	 if (type === SyncJobType.FULL || type === SyncJobType.ANALYSIS) {
 244	 pipelinePromises.push(
 245	 this.executeCodeAnalysisPipeline(codebaseId, files, totalBatches, syncJobId)
 246	 );
 247	 }
 248	
 249	 // Wait for all pipelines to complete
 250	 const results = await Promise.allSettled(pipelinePromises);
 251	
 252	 // Check for failures
 253	 const failures = results.filter(result => result.status === 'rejected');
 254	 if (failures.length > 0) {
 255	 this.logger.error(`${failures.length} pipelines failed:`, failures);
 256	 // Don't throw - allow partial success
 257	 }
 258	
 259	 // Update progress
 260	 await this.updateSyncProgress(syncJobId, codebaseId, 90, 'Parallel processing completed');
 261	
 262	 this.logger.log(`Parallel processing pipelines completed for codebase: ${codebaseId}`);
 263	 } catch (error) {
 264	 this.logger.error(`Parallel processing failed for codebase ${codebaseId}:`, error);
 265	 throw error;
 266	 }
 267	 }
 268	
 269	 /**
 270	 * Execute file processing pipeline
 271	 */
 272	 private async executeFileProcessingPipeline(
 273	 codebaseId: string,
 274	 files: any[],
 275	 totalBatches: number,
 276	 syncJobId: string,
 277	 ): Promise<void> {
 278	 this.logger.log(`Starting file processing pipeline for ${files.length} files`);
 279	
 280	 const batchSize = this.config?.performance?.maxFilesPerBatch || 100;
 281	 const fileBatches = this.createBatches(files, batchSize);
 282	
 283	 // Submit file processing jobs
 284	 const fileProcessingPromises = fileBatches.map((batch, index) =>
 285	 this.enhancedQueueService.submitFileProcessingJob({
 286	 codebaseId,
 287	 files: batch.map(f => ({
 288	 path: f.path,
 289	 hash: f.hash || '',
 290	 size: f.size || 0,
 291	 language: f.language,
 292	 storageKey: f.storageKey,
 293	 })),
 294	 batchIndex: index,
 295	 totalBatches,
 296	 syncJobId,
 297	 options: {
 298	 forceReprocess: false,
 299	 validateContent: true,
 300	 extractMetadata: true,
 301	 },
 302	 })
 303	 );
 304	
 305	 // Wait for file processing to complete
 306	 await Promise.all(fileProcessingPromises);
 307	
 308	 // Submit embedding generation jobs
 309	 const embeddingPromises = fileBatches.map((batch, index) =>
 310	 this.enhancedQueueService.submitEmbeddingJob({
 311	 codebaseId,
 312	 files: batch.map(f => ({
 313	 path: f.path,
 314	 language: f.language || 'text',
 315	 storageKey: f.storageKey || f.path,
 316	 size: f.size || 0,
 317	 })),
 318	 batchIndex: index,
 319	 totalBatches,
 320	 syncJobId,
 321	 options: {
 322	 chunkingStrategy: 'hybrid',
 323	 batchSize: this.config?.embeddingConfig?.batchSize || 20,
 324	 forceRegenerate: false,
 325	 },
 326	 })
 327	 );
 328	
 329	 // Wait for embedding generation to complete
 330	 await Promise.all(embeddingPromises);
 331	
 332	 this.logger.log(`File processing pipeline completed for ${files.length} files`);
 333	 }
