 335	 /**
 336	 * Execute code analysis pipeline
 337	 */
 338	 private async executeCodeAnalysisPipeline(
 339	 codebaseId: string,
 340	 files: any[],
 341	 totalBatches: number,
 342	 syncJobId: string,
 343	 ): Promise<void> {
 344	 this.logger.log(`Starting code analysis pipeline for ${files.length} files`);
 345	
 346	 const batchSize = this.config?.performance?.maxFilesPerBatch || 100;
 347	 const fileBatches = this.createBatches(files, batchSize);
 348	
 349	 const codeAnalysisPromises = fileBatches.map((batch, index) =>
 350	 this.enhancedQueueService.submitCodeAnalysisJob({
 351	 codebaseId,
 352	 files: batch.map(f => ({
 353	 path: f.path,
 354	 language: f.language || 'text',
 355	 storageKey: f.storageKey || f.path,
 356	 })),
 357	 analysisType: 'both', // SCIP + Tree-sitter
 358	 batchIndex: index,
 359	 totalBatches,
 360	 syncJobId,
 361	 options: {
 362	 extractSymbols: true,
 363	 buildRelationships: true,
 364	 generateIndex: true,
 365	 },
 366	 })
