 68	
 69	 // Acquire distributed lock for this codebase
 70	 const lockResource = `codegraph-processing:${codebaseId}`;
 71	 const lockResult = await this.distributedLockService.acquireLock(lockResource, {
 72	 ttl: 1800000, // 30 minutes
 73	 maxRetries: 3,
 74	 });
 75	
 76	 if (!lockResult.acquired) {
 77	 throw new Error(`Failed to acquire lock for codebase ${codebaseId}: ${lockResult.error}`);
 78	 }
