Path: src/modules/indexing/services/neo4j.service.ts
 1	import { Injectable, Inject, LoggerService, OnModuleDestroy } from '@nestjs/common';
 2	import { WINSTON_MODULE_NEST_PROVIDER } from 'nest-winston';
 3	import { Driver, Session, auth, driver as neo4jDriver } from 'neo4j-driver';
 4	import { GraphUpdateConfig } from '../entities/index-job.entity';
 5	
 6	export interface Neo4jNode {
 7	 id: string;
 8	 labels: string[];
 9	 properties: Record<string, any>;
 10	}
 11	
 12	export interface Neo4jRelationship {
 13	 type: string;
 14	 startNodeId: string;
 15	 endNodeId: string;
 16	 properties?: Record<string, any>;
 17	}
 18	
 19	export interface GraphOperationResult {
 20	 nodesCreated: number;
 21	 nodesUpdated: number;
 22	 relationshipsCreated: number;
 23	 relationshipsUpdated: number;
 24	 nodesDeleted?: number;
 25	 relationshipsDeleted?: number;
 26	}
 27	
 28	@Injectable()
 29	export class Neo4jService implements OnModuleDestroy {
 30	 private driver: Driver | null = null;
 31	 private isConnected = false;
 32	 private database: string = 'neo4j';
 33	
 34	 constructor(
 35	 @Inject(WINSTON_MODULE_NEST_PROVIDER)
 36	 private readonly logger: LoggerService
 37	 ) {}
 38	
 39	 /**
 40	 * Initialize connection to Neo4j
 41	 */
 42	 async connect(config: GraphUpdateConfig): Promise<void> {
 43	 if (this.isConnected && this.driver) {
 44	 return;
 45	 }
 46	
 47	 try {
 48	 this.logger.debug(`[NEO4J] Connecting to Neo4j at ${config.url}`);
 49	
 50	 this.database = config.database;
 51	 this.driver = neo4jDriver(
 52	 config.url,
 53	 auth.basic(config.username, config.password),
 54	 {
 55	 maxConnectionLifetime: 30 * 60 * 1000, // 30 minutes
 56	 maxConnectionPoolSize: 50,
 57	 connectionAcquisitionTimeout: 60000, // 60 seconds
 58	 }
 59	 );
 60	
 61	 // Test connection
 62	 const session = this.driver.session({ database: this.database });
 63	 await session.run('RETURN 1');
 64	 await session.close();
 65	
 66	 this.isConnected = true;
 67	 this.logger.log(`[NEO4J] Successfully connected to Neo4j database: ${config.database}`);
 68	 } catch (error) {
 69	 this.logger.error(`[NEO4J] Failed to connect to Neo4j`, {
 70	 error: error instanceof Error ? error.message : String(error),
 71	 url: config.url,
 72	 database: config.database
 73	 });
 74	 throw error;
 75	 }
 76	 }
 77	
 78	 /**
 79	 * Disconnect from Neo4j
 80	 */
 81	 async disconnect(): Promise<void> {
 82	 if (this.driver) {
 83	 await this.driver.close();
 84	 this.driver = null;
 85	 this.isConnected = false;
 86	 this.logger.debug(`[NEO4J] Disconnected from Neo4j`);
 87	 }
 88	 }
 89	
 90	 /**
 91	 * Get a new session
 92	 */
 93	 private getSession(): Session {
 94	 if (!this.driver || !this.isConnected) {
 95	 throw new Error('Neo4j driver not connected. Call connect() first.');
 96	 }
 97	 return this.driver.session({ database: this.database });
 98	 }
 99	
 100	 /**
 101	 * Create or update a project node
 102	 */
 103	 async createOrUpdateProject(projectId: string, projectName: string): Promise<void> {
 104	 const session = this.getSession();
 105	 try {
 106	 await session.run(
 107	 `
 108	 MERGE (p:Project {projectId: $projectId})
 109	 SET p.name = $projectName,
 110	 p.updatedAt = datetime()
 111	 `,
 112	 { projectId, projectName }
 113	 );
 114	 this.logger.debug(`[NEO4J] Created/updated project node: ${projectId}`);
 115	 } finally {
 116	 await session.close();
 117	 }
 118	 }
...
