 13	@Injectable()
 14	export class TaskConfigService {
 15	 constructor(private configService: ConfigService) {}
 16	
 17	 /**
 18	 * Get configuration for a specific task
 19	 */
 20	 getTaskConfig<T extends TaskConfig>(
 21	 taskName: string,
 22	 jobType: IndexJobType,
 23	 customConfig?: Partial<T>
 24	 ): T {
 25	 const baseConfig = this.getBaseTaskConfig(taskName, jobType);
 26	 
 27	 if (customConfig) {
 28	 return this.deepMerge(baseConfig, customConfig) as T;
 29	 }
 30	 
 31	 return baseConfig as T;
 32	 }
