 10	
 11	@Injectable()
 12	export class GraphService {
 13	 constructor(
 14	 private readonly neo4jService: Neo4jService,
 15	 @InjectRepository(TekProject)
 16	 private readonly projectRepository: Repository<TekProject>,
 17	 @InjectRepository(Codebase)
 18	 private readonly codebaseRepository: Repository<Codebase>,
 19	 @Inject(WINSTON_MODULE_NEST_PROVIDER)
 20	 private readonly logger: LoggerService
 21	 ) {}
 22	
 23	 /**
 24	 * Initialize the graph database with constraints and indexes
 25	 */
 26	 async initializeGraph(config: GraphUpdateConfig): Promise<void> {
 27	 await this.neo4jService.connect(config);
 28	 await this.neo4jService.createConstraintsAndIndexes();
 29	 this.logger.log(`[GRAPH-SERVICE] Graph database initialized`);
