 164	 TypeUsageVisitor typeUsageVisitor = new TypeUsageVisitor(result, codebaseName);
 165	 
 166	 for (CtType<?> type : model.getAllTypes()) {
 167	 if (type instanceof CtClass) {
 168	 CtClass<?> clazz = (CtClass<?>) type;
