 172	 method.accept(typeUsageVisitor);
 173	 }
 174	 for (CtConstructor<?> constructor : clazz.getConstructors()) {
 175	 constructor.accept(callGraphVisitor);
 176	 constructor.accept(typeUsageVisitor);
 177	 }
