 1	import { Injectable, Inject, LoggerService } from '@nestjs/common';
 2	import { InjectRepository } from '@nestjs/typeorm';
 3	import { Repository } from 'typeorm';
 4	import { WINSTON_MODULE_NEST_PROVIDER } from 'nest-winston';
 5	import { TekProject, Codebase } from '@/entities';
 6	import { Neo4jService, GraphOperationResult } from './neo4j.service';
 7	import { GraphUpdateConfig } from '../entities/index-job.entity';
 8	import { StandardizedFile, StandardizedSymbol } from './parser-output-transformer.service';
 9	import { createHash } from 'crypto';
 10	
 11	@Injectable()
 12	export class GraphService {
 13	 constructor(
 14	 private readonly neo4jService: Neo4jService,
 15	 @InjectRepository(TekProject)
 16	 private readonly projectRepository: Repository<TekProject>,
