 23	 /**
 24	 * Initialize the graph database with constraints and indexes
 25	 */
 26	 async initializeGraph(config: GraphUpdateConfig): Promise<void> {
 27	 await this.neo4jService.connect(config);
 28	 await this.neo4jService.createConstraintsAndIndexes();
 29	 this.logger.log(`[GRAPH-SERVICE] Graph database initialized`);
 30	 }
 31	
 32	 /**
 33	 * Update the graph with parsed files from a codebase
 34	 */
 35	 async updateCodebaseGraph(
 36	 codebaseId: string,
 37	 files: StandardizedFile[],
 38	 config: GraphUpdateConfig
 39	 ): Promise<GraphOperationResult> {
 40	 await this.neo4jService.connect(config);
 41	
 42	 // Get codebase and project information
 43	 const codebase = await this.codebaseRepository.findOne({
 44	 where: { id: codebaseId },
 45	 relations: ['project']
 46	 });
 47	
 48	 if (!codebase || !codebase.project) {
 49	 throw new Error(`Codebase ${codebaseId} or its project not found`);
 50	 }
 51	
 52	 this.logger.debug(`[GRAPH-SERVICE] Updating graph for codebase: ${codebase.name}`);
 53	
 54	 // Create/update project and codebase nodes
 55	 await this.neo4jService.createOrUpdateProject(
 56	 codebase.project.id,
 57	 codebase.project.name
 58	 );
 59	
 60	 await this.neo4jService.createOrUpdateCodebase(
 61	 codebase.project.id,
 62	 codebase.id,
 63	 codebase.name,
 64	 codebase.gitlabUrl,
 65	 codebase.language,
 66	 undefined, // framework not available in entity
 67	 codebase.lastSyncCommit
 68	 );
 69	
 70	 // Process files in batches
 71	 const batchSize = config.batchSize;
 72	 let totalResult: GraphOperationResult = {
 73	 nodesCreated: 0,
 74	 nodesUpdated: 0,
 75	 relationshipsCreated: 0,
 76	 relationshipsUpdated: 0
 77	 };
 78	
 79	 for (let i = 0; i < files.length; i += batchSize) {
 80	 const batch = files.slice(i, i + batchSize);
 81	 const batchResult = await this.processBatch(codebaseId, batch);
 82	 
 83	 totalResult.nodesCreated += batchResult.nodesCreated;
 84	 totalResult.nodesUpdated += batchResult.nodesUpdated;
 85	 totalResult.relationshipsCreated += batchResult.relationshipsCreated;
 86	 totalResult.relationshipsUpdated += batchResult.relationshipsUpdated;
 87	
 88	 this.logger.debug(`[GRAPH-SERVICE] Processed batch ${Math.floor(i / batchSize) + 1}/${Math.ceil(files.length / batchSize)}`);
 89	 }
 90	
 91	 this.logger.log(`[GRAPH-SERVICE] Graph update completed`, {
 92	 codebaseId,
 93	 filesProcessed: files.length,
 94	 ...totalResult
 95	 });
 96	
 97	 return totalResult;
 98	 }
 99	
 100	 /**
 101	 * Process a batch of files
 102	 */
 103	 private async processBatch(codebaseId: string, files: StandardizedFile[]): Promise<GraphOperationResult> {
 104	 const queries: Array<{ query: string; parameters: Record<string, any> }> = [];
 105	
 106	 for (const file of files) {
 107	 // Create file node
 108	 const fileChecksum = this.calculateFileChecksum(file);
 109	 queries.push({
 110	 query: `
 111	 MATCH (c:Codebase {id: $codebaseId})
 112	 MERGE (f:File {path: $filePath})
 113	 SET f.fileName = $fileName,
 114	 f.packageName = $packageName,
 115	 f.language = $language,
 116	 f.checksum = $checksum,
 117	 f.updatedAt = datetime()
 118	 MERGE (c)-[:CONTAINS_FILE]->(f)
 119	 `,
 120	 parameters: {
 121	 codebaseId,
 122	 filePath: file.path,
 123	 fileName: file.fileName,
 124	 packageName: file.packageName,
 125	 language: file.language,
 126	 checksum: fileChecksum
 127	 }
 128	 });
 129	
 130	 // Process symbols in the file
 131	 for (const symbol of file.symbols) {
 132	 const symbolQueries = this.createSymbolQueries(file.path, symbol);
 133	 queries.push(...symbolQueries);
 134	 }
 135	
 136	 // Process relationships
 137	 for (const relationship of file.relationships) {
 138	 // Extract entity names from spoon IDs for matching
 139	 const sourceName = this.extractEntityNameFromSpoonId(relationship.source);
 140	 const targetName = this.extractEntityNameFromSpoonId(relationship.target);
 141	
 142	 if (sourceName && targetName) {
 143	 queries.push({
 144	 query: `
 145	 MATCH (source), (target)
 146	 WHERE (source:Class OR source:Method OR source:Interface OR source:Variable)
 147	 AND (source.name = $sourceName OR source.fullyQualifiedName = $sourceFullName)
 148	 AND (target:Class OR target:Method OR target:Interface OR target:Variable)
 149	 AND (target.name = $targetName OR target.fullyQualifiedName = $targetFullName)
 150	 MERGE (source)-[:${relationship.type.toUpperCase()}]->(target)
 151	 `,
 152	 parameters: {
 153	 sourceName,
 154	 targetName,
 155	 sourceFullName: this.extractFullyQualifiedNameFromSpoonId(relationship.source),
 156	 targetFullName: this.extractFullyQualifiedNameFromSpoonId(relationship.target)
 157	 }
 158	 });
 159	 }
 160	 }
 161	 }
 162	
 163	 return await this.neo4jService.executeBatch(queries);
 164	 }
 165	
 166	 /**
 167	 * Create Cypher queries for a symbol
 168	 */
 169	 private createSymbolQueries(filePath: string, symbol: StandardizedSymbol): Array<{ query: string; parameters: Record<string, any> }> {
 170	 const queries: Array<{ query: string; parameters: Record<string, any> }> = [];
 171	 const symbolId = this.generateSymbolId(filePath, symbol);
 172	
 173	 switch (symbol.type) {
 174	 case 'class':
 175	 queries.push({
 176	 query: `
 177	 MATCH (f:File {path: $filePath})
 178	 MERGE (c:Class {id: $symbolId})
 179	 SET c.name = $name,
 180	 c.fullyQualifiedName = $fullyQualifiedName,
 181	 c.visibility = $visibility,
 182	 c.isStatic = $isStatic,
 183	 c.isAbstract = $isAbstract,
 184	 c.line = $line,
 185	 c.updatedAt = datetime()
 186	 MERGE (f)-[:DEFINES_CLASS]->(c)
 187	 `,
 188	 parameters: {
 189	 filePath,
 190	 symbolId,
 191	 name: symbol.name,
 192	 fullyQualifiedName: this.getFullyQualifiedName(filePath, symbol),
 193	 visibility: symbol.visibility,
 194	 isStatic: symbol.isStatic,
 195	 isAbstract: symbol.isAbstract,
 196	 line: symbol.line
 197	 }
 198	 });
 199	 break;
 200	
 201	 case 'interface':
 202	 queries.push({
 203	 query: `
 204	 MATCH (f:File {path: $filePath})
 205	 MERGE (i:Interface {id: $symbolId})
 206	 SET i.name = $name,
 207	 i.fullyQualifiedName = $fullyQualifiedName,
 208	 i.line = $line,
 209	 i.updatedAt = datetime()
 210	 MERGE (f)-[:DEFINES_INTERFACE]->(i)
 211	 `,
 212	 parameters: {
 213	 filePath,
 214	 symbolId,
 215	 name: symbol.name,
 216	 fullyQualifiedName: this.getFullyQualifiedName(filePath, symbol),
 217	 line: symbol.line
 218	 }
 219	 });
 220	 break;
 221	
 222	 case 'method':
 223	 case 'function':
 224	 const signature = this.buildMethodSignature(symbol);
 225	 queries.push({
 226	 query: `
 227	 MATCH (f:File {path: $filePath})
 228	 MERGE (m:Method {id: $symbolId})
 229	 SET m.name = $name,
 230	 m.signature = $signature,
 231	 m.returnType = $returnType,
 232	 m.visibility = $visibility,
 233	 m.isStatic = $isStatic,
 234	 m.isAbstract = $isAbstract,
 235	 m.line = $line,
 236	 m.updatedAt = datetime()
 237	 MERGE (f)-[:DEFINES_METHOD]->(m)
 238	 `,
 239	 parameters: {
 240	 filePath,
 241	 symbolId,
 242	 name: symbol.name,
 243	 signature,
 244	 returnType: symbol.returnType,
 245	 visibility: symbol.visibility,
 246	 isStatic: symbol.isStatic,
 247	 isAbstract: symbol.isAbstract,
 248	 line: symbol.line
 249	 }
 250	 });
 251	 break;
 252	
 253	 case 'field':
 254	 case 'property':
 255	 case 'variable':
 256	 queries.push({
 257	 query: `
 258	 MATCH (f:File {path: $filePath})
 259	 MERGE (v:Variable {id: $symbolId})
 260	 SET v.name = $name,
 261	 v.type = $type,
 262	 v.visibility = $visibility,
 263	 v.isStatic = $isStatic,
 264	 v.line = $line,
 265	 v.updatedAt = datetime()
 266	 MERGE (f)-[:DEFINES_VARIABLE]->(v)
 267	 `,
 268	 parameters: {
 269	 filePath,
 270	 symbolId,
 271	 name: symbol.name,
 272	 type: symbol.returnType || 'unknown',
 273	 visibility: symbol.visibility,
 274	 isStatic: symbol.isStatic,
 275	 line: symbol.line
 276	 }
 277	 });
 278	 break;
 279	 }
 280	
 281	 return queries;
 282	 }
 283	
 284	 /**
 285	 * Generate a unique ID for a symbol
 286	 */
 287	 private generateSymbolId(filePath: string, symbol: StandardizedSymbol): string {
 288	 const content = `${filePath}:${symbol.type}:${symbol.name}:${symbol.line || 0}`;
 289	 return createHash('sha256').update(content).digest('hex').substring(0, 16);
 290	 }
 291	
 292	 /**
 293	 * Get fully qualified name for a symbol
 294	 */
 295	 private getFullyQualifiedName(filePath: string, symbol: StandardizedSymbol): string {
 296	 // Extract package from file path or use symbol name
 297	 const pathParts = filePath.split('/');
 298	 const fileName = pathParts[pathParts.length - 1].replace(/\.(java|ts|js)$/, '');
 299	 return `${fileName}.${symbol.name}`;
 300	 }
 301	
 302	 /**
 303	 * Build method signature from symbol
 304	 */
 305	 private buildMethodSignature(symbol: StandardizedSymbol): string {
 306	 const params = symbol.parameters?.map(p => `${p.name}: ${p.type}`).join(', ') || '';
 307	 return `${symbol.name}(${params})${symbol.returnType ? `: ${symbol.returnType}` : ''}`;
 308	 }
 309	
 310	 /**
 311	 * Calculate checksum for a file
 312	 */
 313	 private calculateFileChecksum(file: StandardizedFile): string {
 314	 const content = JSON.stringify({
 315	 path: file.path,
 316	 symbols: file.symbols.length,
 317	 relationships: file.relationships.length
 318	 });
 319	 return createHash('md5').update(content).digest('hex');
 320	 }
 321	
 322	 /**
 323	 * Handle deleted files by removing them and their related nodes from the graph
 324	 */
 325	 async handleDeletedFiles(codebaseId: string, deletedFilePaths: string[]): Promise<GraphOperationResult> {
 326	 if (deletedFilePaths.length === 0) {
 327	 return { nodesCreated: 0, nodesUpdated: 0, relationshipsCreated: 0, relationshipsUpdated: 0 };
 328	 }
 329	
 330	 this.logger.debug(`[GRAPH-SERVICE] Handling ${deletedFilePaths.length} deleted files for codebase: ${codebaseId}`);
 331	 
 332	 return await this.neo4jService.deleteFilesFromCodebase(codebaseId, deletedFilePaths);
 333	 }
 334	}
