 detectedLanguage = Language.JAVA;
 break;
 // ... other cases
 }
}
```

#### 3. **Fixed Graph Queries**
```typescript
// OLD (Broken)
WHERE source.name = $sourceName AND target.name = $targetName

// NEW (Fixed) 
WHERE (source.name = $sourceName OR source.fullyQualifiedName = $sourceFullName)
 AND (target.name = $targetName OR target.fullyQualifiedName = $targetFullName)
```

#### 4. **Added Spoon ID Parsing Utilities**
```typescript
private extractEntityNameFromSpoonId(spoonId: string): string | null {
 // "test-project:class:com.example.User" → "User"
 const parts = spoonId.split(':');
 if (parts.length >= 3) {
 const fullyQualifiedName = parts.slice(2).join(':');
 const nameParts = fullyQualifiedName.split('.');
 return nameParts[nameParts.length - 1];
 }
 return null;
}
```

