 130	 // Process symbols in the file
 131	 for (const symbol of file.symbols) {
 132	 const symbolQueries = this.createSymbolQueries(file.path, symbol);
 133	 queries.push(...symbolQueries);
 134	 }
 135	
 136	 // Process relationships
 137	 for (const relationship of file.relationships) {
 138	 queries.push({
 139	 query: `
 140	 MATCH (source), (target)
 141	 WHERE (source:Class OR source:Method OR source:Interface) AND source.name = $sourceName
 142	 AND (target:Class OR target:Method OR target:Interface) AND target.name = $targetName
 143	 MERGE (source)-[:${relationship.type.toUpperCase()}]->(target)
 144	 `,
 145	 parameters: {
 146	 sourceName: relationship.source,
 147	 targetName: relationship.target
 148	 }
 149	 });
 150	 }
 151	 }
 152	
 153	 return await this.neo4jService.executeBatch(queries);
 154	 }
 155	
 156	 /**
 157	 * Create Cypher queries for a symbol
 158	 */
 159	 private createSymbolQueries(filePath: string, symbol: StandardizedSymbol): Array<{ query: string; parameters: Record<string, any> }> {
 160	 const queries: Array<{ query: string; parameters: Record<string, any> }> = [];
 161	 const symbolId = this.generateSymbolId(filePath, symbol);
 162	
 163	 switch (symbol.type) {
 164	 case 'class':
 165	 queries.push({
 166	 query: `
 167	 MATCH (f:File {path: $filePath})
 168	 MERGE (c:Class {id: $symbolId})
 169	 SET c.name = $name,
 170	 c.fullyQualifiedName = $fullyQualifiedName,
 171	 c.visibility = $visibility,
 172	 c.isStatic = $isStatic,
 173	 c.isAbstract = $isAbstract,
 174	 c.line = $line,
 175	 c.updatedAt = datetime()
 176	 MERGE (f)-[:DEFINES_CLASS]->(c)
 177	 `,
 178	 parameters: {
 179	 filePath,
 180	 symbolId,
 181	 name: symbol.name,
 182	 fullyQualifiedName: this.getFullyQualifiedName(filePath, symbol),
 183	 visibility: symbol.visibility,
 184	 isStatic: symbol.isStatic,
 185	 isAbstract: symbol.isAbstract,
 186	 line: symbol.line
 187	 }
 188	 });
 189	 break;
 190	
 191	 case 'interface':
 192	 queries.push({
 193	 query: `
 194	 MATCH (f:File {path: $filePath})
 195	 MERGE (i:Interface {id: $symbolId})
 196	 SET i.name = $name,
 197	 i.fullyQualifiedName = $fullyQualifiedName,
 198	 i.line = $line,
 199	 i.updatedAt = datetime()
 200	 MERGE (f)-[:DEFINES_INTERFACE]->(i)
 201	 `,
 202	 parameters: {
 203	 filePath,
 204	 symbolId,
 205	 name: symbol.name,
 206	 fullyQualifiedName: this.getFullyQualifiedName(filePath, symbol),
 207	 line: symbol.line
 208	 }
 209	 });
 210	 break;
 211	
 212	 case 'method':
 213	 case 'function':
 214	 const signature = this.buildMethodSignature(symbol);
 215	 queries.push({
 216	 query: `
 217	 MATCH (f:File {path: $filePath})
 218	 MERGE (m:Method {id: $symbolId})
 219	 SET m.name = $name,
 220	 m.signature = $signature,
 221	 m.returnType = $returnType,
 222	 m.visibility = $visibility,
 223	 m.isStatic = $isStatic,
 224	 m.isAbstract = $isAbstract,
 225	 m.line = $line,
 226	 m.updatedAt = datetime()
 227	 MERGE (f)-[:DEFINES_METHOD]->(m)
 228	 `,
 229	 parameters: {
 230	 filePath,
 231	 symbolId,
 232	 name: symbol.name,
 233	 signature,
 234	 returnType: symbol.returnType,
 235	 visibility: symbol.visibility,
 236	 isStatic: symbol.isStatic,
 237	 isAbstract: symbol.isAbstract,
 238	 line: symbol.line
 239	 }
 240	 });
 241	 break;
 242	
 243	 case 'field':
 244	 case 'property':
 245	 case 'variable':
 246	 queries.push({
 247	 query: `
 248	 MATCH (f:File {path: $filePath})
 249	 MERGE (v:Variable {id: $symbolId})
 250	 SET v.name = $name,
 251	 v.type = $type,
 252	 v.visibility = $visibility,
 253	 v.isStatic = $isStatic,
 254	 v.line = $line,
 255	 v.updatedAt = datetime()
 256	 MERGE (f)-[:DEFINES_VARIABLE]->(v)
 257	 `,
 258	 parameters: {
 259	 filePath,
 260	 symbolId,
 261	 name: symbol.name,
 262	 type: symbol.returnType || 'unknown',
 263	 visibility: symbol.visibility,
 264	 isStatic: symbol.isStatic,
 265	 line: symbol.line
 266	 }
 267	 });
 268	 break;
 269	 }
 270	
 271	 return queries;
 272	 }
 273	
 274	 /**
 275	 * Generate a unique ID for a symbol
 276	 */
 277	 private generateSymbolId(filePath: string, symbol: StandardizedSymbol): string {
 278	 const content = `${filePath}:${symbol.type}:${symbol.name}:${symbol.line || 0}`;
 279	 return createHash('sha256').update(content).digest('hex').substring(0, 16);
 280	 }
 281	
 282	 /**
 283	 * Get fully qualified name for a symbol
 284	 */
 285	 private getFullyQualifiedName(filePath: string, symbol: StandardizedSymbol): string {
 286	 // Extract package from file path or use symbol name
 287	 const pathParts = filePath.split('/');
 288	 const fileName = pathParts[pathParts.length - 1].replace(/\.(java|ts|js)$/, '');
 289	 return `${fileName}.${symbol.name}`;
 290	 }
 291	
 292	 /**
 293	 * Build method signature from symbol
 294	 */
 295	 private buildMethodSignature(symbol: StandardizedSymbol): string {
 296	 const params = symbol.parameters?.map(p => `${p.name}: ${p.type}`).join(', ') || '';
 297	 return `${symbol.name}(${params})${symbol.returnType ? `: ${symbol.returnType}` : ''}`;
 298	 }
 299	
 300	 /**
 301	 * Calculate checksum for a file
 302	 */
 303	 private calculateFileChecksum(file: StandardizedFile): string {
 304	 const content = JSON.stringify({
 305	 path: file.path,
 306	 symbols: file.symbols.length,
 307	 relationships: file.relationships.length
 308	 });
 309	 return createHash('md5').update(content).digest('hex');
 310	 }
 311	
 312	 /**
 313	 * Handle deleted files by removing them and their related nodes from the graph
 314	 */
 315	 async handleDeletedFiles(codebaseId: string, deletedFilePaths: string[]): Promise<GraphOperationResult> {
 316	 if (deletedFilePaths.length === 0) {
 317	 return { nodesCreated: 0, nodesUpdated: 0, relationshipsCreated: 0, relationshipsUpdated: 0 };
 318	 }
 319	
 320	 this.logger.debug(`[GRAPH-SERVICE] Handling ${deletedFilePaths.length} deleted files for codebase: ${codebaseId}`);
 321	 
 322	 return await this.neo4jService.deleteFilesFromCodebase(codebaseId, deletedFilePaths);
 323	 }
