 121	 * Create or update a codebase node
 122	 */
 123	 async createOrUpdateCodebase(
 124	 projectId: string,
 125	 codebaseId: string,
 126	 codebaseName: string,
 127	 gitUrl: string,
 128	 language: string,
 129	 framework?: string,
 130	 lastIndexedCommit?: string
 131	 ): Promise<void> {
 132	 const session = this.getSession();
