 134	 const query = `
 135	 MATCH (p:Project {projectId: $projectId})
 136	 MERGE (c:Codebase {id: $codebaseId})
 137	 SET c.name = $codebaseName,
 138	 c.gitUrl = $gitUrl,
 139	 c.language = $language,
 140	 c.lastIndexedCommit = $lastIndexedCommit,
 141	 c.updatedAt = datetime()
 142	 ${framework ? ', c.framework = $framework' : ''}
 143	 MERGE (p)-[:HAS_CODEBASE]->(c)
 144	 `;
 145	
 146	 const parameters: Record<string, any> = {
 147	 projectId,
 148	 codebaseId,
 149	 codebaseName,
 150	 gitUrl,
 151	 language,
 152	 lastIndexedCommit
 153	 };
 154	
 155	 if (framework) {
 156	 parameters.framework = framework;
 157	 }
