 316	function isTestFile(filePath: string): boolean {
 317	 const testPatterns = [
 318	 /\.test\.(ts|tsx|js|jsx|java)$/,
 319	 /\.spec\.(ts|tsx|js|jsx|java)$/,
 320	 /__tests__\//,
 321	 /\/test\//,
 322	 /\/tests\//,
 323	 /Test\.java$/,
 324	 /Tests\.java$/
 325	 ];
 326	 
 327	 return testPatterns.some(pattern => pattern.test(filePath));
 328	}
