 204	 private void extractDependencies() {
 205	 logger.info("Extracting project dependencies");
 206	 List<DependencyNode> dependencies = dependencyProcessor.extractDependencies();
 207	 result.setDependencies(dependencies);
 208	
 209	 // Create DEPENDS_ON relationships (Project -> Dependency)
 210	 for (DependencyNode dependency : dependencies) {
 211	 Relationship dependsOnRel = relationshipProcessor.createDependsOnRelationship(dependency.getId());
 212	 if (dependsOnRel != null) {
 213	 result.addRelationship(dependsOnRel);
 214	 }
 215	 }
 216	
 217	 logger.info("Found {} dependencies", dependencies.size());
 218	 }
