 37	 parsingDurationMs: number;
 38	 framework: string;
 39	 detectedFrameworks: string[];
 40	 statistics: StatisticsNode;
 41	 configuration: Record<string, any>;
 42	 errors: string[] | null;
 43	 warnings: string[] | null;
 44	}
 45	
 46	export interface StatisticsNode {
 47	 totalFiles: number;
 48	 totalLines: number;
 49	 totalClasses: number;
 50	 totalInterfaces: number;
 51	 totalMethods: number;
 52	 totalFields: number;
