 54	 testCoverage: number;
 55	 duplicateLines: number;
 56	 averageMethodComplexity: number;
 57	 maxMethodComplexity: number;
 58	 linesOfCode: number;
 59	 commentLines: number;
 60	 blankLines: number;
 61	}
