 351	// ============================================================================
 352	// UTILITY FUNCTIONS & LOADER CLASS
 353	// ============================================================================
 354	
 355	/**
 356	 * Utility class for loading and working with Spoon Parser results
 357	 */
 358	export class SpoonParserLoader {
 359	 private result: SpoonParserResult | null = null;
 360	
 361	 /**
 362	 * Load Spoon Parser result from JSON string
 363	 */
 364	 public loadFromJson(jsonString: string): SpoonParserResult {
 365	 try {
 366	 this.result = JSON.parse(jsonString) as SpoonParserResult;
 367	 return this.result;
 368	 } catch (error) {
 369	 throw new Error(`Failed to parse Spoon Parser JSON: ${error}`);
 370	 }
 371	 }
 372	
 373	 /**
 374	 * Load Spoon Parser result from file (Node.js environment)
 375	 */
 376	 public async loadFromFile(filePath: string): Promise<SpoonParserResult> {
 377	 try {
 378	 const fs = await import('fs/promises');
 379	 const jsonString = await fs.readFile(filePath, 'utf-8');
 380	 return this.loadFromJson(jsonString);
 381	 } catch (error) {
 382	 throw new Error(`Failed to load Spoon Parser file: ${error}`);
 383	 }
 384	 }
 385	
 386	 /**
 387	 * Get the loaded result
 388	 */
 389	 public getResult(): SpoonParserResult | null {
 390	 return this.result;
 391	 }
 392	
 393	 /**
 394	 * Get classes by framework type
 395	 */
 396	 public getClassesByFramework(frameworkType: keyof Pick<ClassNode,
 397	 'isController' | 'isService' | 'isRepository' | 'isComponent' | 'isConfiguration' | 'isEntity'>): ClassNode[] {
 398	 if (!this.result) return [];
 399	 return this.result.classes.filter(cls => cls[frameworkType]);
 400	 }
 401	
 402	 /**
 403	 * Get test classes
 404	 */
 405	 public getTestClasses(): ClassNode[] {
 406	 if (!this.result) return [];
 407	 return this.result.classes.filter(cls => cls.isTestClass);
 408	 }
 409	
 410	 /**
 411	 * Get methods by class ID
 412	 */
 413	 public getMethodsByClassId(classId: string): MethodNode[] {
 414	 if (!this.result) return [];
 415	 return this.result.methods.filter(method =>
 416	 method.id.includes(classId.replace('class:', 'method:')));
 417	 }
 418	
 419	 /**
 420	 * Get fields by class ID
 421	 */
 422	 public getFieldsByClassId(classId: string): FieldNode[] {
 423	 if (!this.result) return [];
 424	 return this.result.fields.filter(field =>
 425	 field.id.includes(classId.replace('class:', 'field:')));
 426	 }
 427	
 428	 /**
 429	 * Get relationships by source ID
 430	 */
 431	 public getRelationshipsBySourceId(sourceId: string): Relationship[] {
 432	 if (!this.result) return [];
 433	 return this.result.relationships.filter(rel => rel.sourceId === sourceId);
 434	 }
 435	
 436	 /**
 437	 * Get relationships by target ID
 438	 */
 439	 public getRelationshipsByTargetId(targetId: string): Relationship[] {
 440	 if (!this.result) return [];
 441	 return this.result.relationships.filter(rel => rel.targetId === targetId);
 442	 }
 443	
 444	 /**
 445	 * Get relationships by type
 446	 */
 447	 public getRelationshipsByType(type: RelationshipType): Relationship[] {
 448	 if (!this.result) return [];
 449	 return this.result.relationships.filter(rel => rel.type === type);
 450	 }
 451	
 452	 /**
 453	 * Get API endpoints by HTTP method
 454	 */
 455	 public getEndpointsByHttpMethod(method: string): APIEndpointNode[] {
 456	 if (!this.result) return [];
 457	 return this.result.apiEndpoints.filter(endpoint =>
 458	 endpoint.httpMethod.toLowerCase() === method.toLowerCase());
 459	 }
 460	
 461	 /**
 462	 * Get dependencies by scope
 463	 */
 464	 public getDependenciesByScope(scope: string): DependencyNode[] {
 465	 if (!this.result) return [];
 466	 return this.result.dependencies.filter(dep => dep.scope === scope);
 467	 }
 468	
 469	 /**
 470	 * Get test methods
 471	 */
 472	 public getTestMethods(): MethodNode[] {
 473	 if (!this.result) return [];
 474	 return this.result.methods.filter(method => method.isTestMethod);
 475	 }
 476	
 477	 /**
 478	 * Get abstract methods
 479	 */
 480	 public getAbstractMethods(): MethodNode[] {
 481	 if (!this.result) return [];
 482	 return this.result.methods.filter(method => method.isAbstract);
 483	 }
 484	
 485	 /**
 486	 * Get static methods
 487	 */
 488	 public getStaticMethods(): MethodNode[] {
 489	 if (!this.result) return [];
 490	 return this.result.methods.filter(method => method.isStatic);
 491	 }
 492	
 493	 /**
 494	 * Get constructors
 495	 */
 496	 public getConstructors(): MethodNode[] {
 497	 if (!this.result) return [];
 498	 return this.result.methods.filter(method => method.isConstructor);
 499	 }
 500	
 501	 /**
 502	 * Get lambda expressions by class ID
 503	 */
 504	 public getLambdaExpressionsByClassId(classId: string): LambdaExpressionNode[] {
 505	 if (!this.result) return [];
 506	 return this.result.lambdaExpressions.filter(lambda => lambda.enclosingClassId === classId);
 507	 }
 508	
 509	 /**
 510	 * Get files by package
 511	 */
 512	 public getFilesByPackage(packageName: string): FileNode[] {
 513	 if (!this.result) return [];
 514	 return this.result.files.filter(file => file.packageName === packageName);
 515	 }
 516	
 517	 /**
 518	 * Get test files
 519	 */
 520	 public getTestFiles(): FileNode[] {
 521	 if (!this.result) return [];
 522	 return this.result.files.filter(file => file.isTestFile);
 523	 }
 524	
 525	 /**
 526	 * Get summary statistics
 527	 */
 528	 public getSummary(): {
 529	 totalClasses: number;
 530	 totalMethods: number;
 531	 totalFields: number;
 532	 totalRelationships: number;
 533	 totalDependencies: number;
 534	 totalApiEndpoints: number;
 535	 totalLambdas: number;
 536	 testClasses: number;
 537	 testMethods: number;
 538	 complexity: number;
 539	 framework: string;
 540	 } {
 541	 if (!this.result) {
 542	 return {
 543	 totalClasses: 0, totalMethods: 0, totalFields: 0, totalRelationships: 0,
 544	 totalDependencies: 0, totalApiEndpoints: 0, totalLambdas: 0,
 545	 testClasses: 0, testMethods: 0, complexity: 0, framework: 'unknown'
 546	 };
 547	 }
 548	
 549	 return {
 550	 totalClasses: this.result.classes.length,
 551	 totalMethods: this.result.methods.length,
 552	 totalFields: this.result.fields.length,
 553	 totalRelationships: this.result.relationships.length,
 554	 totalDependencies: this.result.dependencies.length,
 555	 totalApiEndpoints: this.result.apiEndpoints.length,
 556	 totalLambdas: this.result.lambdaExpressions.length,
 557	 testClasses: this.getTestClasses().length,
 558	 testMethods: this.getTestMethods().length,
 559	 complexity: this.result.metadata.statistics.complexity,
 560	 framework: this.result.metadata.framework
 561	 };
 562	 }
 563	}
 564	
 565	// ============================================================================
 566	// TYPE GUARDS
 567	// ============================================================================
 568	
 569	export function isSpoonParserResult(obj: any): obj is SpoonParserResult {
 570	 return obj &&
 571	 typeof obj === 'object' &&
 572	 'metadata' in obj &&
 573	 'codebaseName' in obj &&
 574	 Array.isArray(obj.files) &&
 575	 Array.isArray(obj.classes) &&
 576	 Array.isArray(obj.methods) &&
 577	 Array.isArray(obj.relationships);
 578	}
 579	
 580	export function isClassNode(obj: any): obj is ClassNode {
 581	 return obj &&
 582	 typeof obj === 'object' &&
 583	 'id' in obj &&
 584	 'name' in obj &&
 585	 'fullyQualifiedName' in obj &&
 586	 typeof obj.isAbstract === 'boolean';
 587	}
 588	
 589	export function isMethodNode(obj: any): obj is MethodNode {
 590	 return obj &&
 591	 typeof obj === 'object' &&
 592	 'id' in obj &&
 593	 'name' in obj &&
 594	 'signature' in obj &&
 595	 typeof obj.isConstructor === 'boolean';
 596	}
