 156	 /**
 157	 * Create Cypher queries for a symbol
 158	 */
 159	 private createSymbolQueries(filePath: string, symbol: StandardizedSymbol): Array<{ query: string; parameters: Record<string, any> }> {
 160	 const queries: Array<{ query: string; parameters: Record<string, any> }> = [];
 161	 const symbolId = this.generateSymbolId(filePath, symbol);
 162	
 163	 switch (symbol.type) {
 164	 case 'class':
 165	 queries.push({
 166	 query: `
 167	 MATCH (f:File {path: $filePath})
 168	 MERGE (c:Class {id: $symbolId})
 169	 SET c.name = $name,
 170	 c.fullyQualifiedName = $fullyQualifiedName,
 171	 c.visibility = $visibility,
 172	 c.isStatic = $isStatic,
 173	 c.isAbstract = $isAbstract,
 174	 c.line = $line,
 175	 c.updatedAt = datetime()
 176	 MERGE (f)-[:DEFINES_CLASS]->(c)
 177	 `,
 178	 parameters: {
 179	 filePath,
 180	 symbolId,
 181	 name: symbol.name,
 182	 fullyQualifiedName: this.getFullyQualifiedName(filePath, symbol),
 183	 visibility: symbol.visibility,
 184	 isStatic: symbol.isStatic,
 185	 isAbstract: symbol.isAbstract,
 186	 line: symbol.line
 187	 }
 188	 });
 189	 break;
 190	
 191	 case 'interface':
 192	 queries.push({
 193	 query: `
 194	 MATCH (f:File {path: $filePath})
 195	 MERGE (i:Interface {id: $symbolId})
 196	 SET i.name = $name,
 197	 i.fullyQualifiedName = $fullyQualifiedName,
 198	 i.line = $line,
 199	 i.updatedAt = datetime()
 200	 MERGE (f)-[:DEFINES_INTERFACE]->(i)
