 274	 /**
 275	 * Generate a unique ID for a symbol
 276	 */
 277	 private generateSymbolId(filePath: string, symbol: StandardizedSymbol): string {
 278	 const content = `${filePath}:${symbol.type}:${symbol.name}:${symbol.line || 0}`;
 279	 return createHash('sha256').update(content).digest('hex').substring(0, 16);
 280	 }
