 304	 startTrace(options: TraceOptions): ITrace {
 305	 try {
 306	 // Return no-op if not initialized or disabled
 307	 if (!this.initialized || !this.client || this.config?.enabled === false) {
 308	 return new NoOpTrace();
 309	 }
 310	 
 311	 // Apply sampling if configured
 312	 if (this.config?.samplingRate !== undefined) {
 313	 if (Math.random() > this.config.samplingRate) {
 314	 console.log(`Trace '${options.name}' excluded by sampling`);
 315	 return new NoOpTrace();
 316	 }
 317	 }
 318	 
 319	 console.log(`Starting trace: ${options.name}`);
 320	 
 321	 // Sanitize trace options
 322	 const sanitized = this.sanitizer?.sanitizeData(options) || options;
 323	 
 324	 // Create underlying Langfuse trace
 325	 const langfuseTrace = this.client.trace({
 326	 id: options.id,
 327	 name: sanitized.name,
 328	 userId: sanitized.userId,
 329	 sessionId: sanitized.sessionId,
 330	 metadata: sanitized.metadata,
 331	 tags: sanitized.tags,
 332	 release: sanitized.release,
 333	 version: sanitized.version,
 334	 input: sanitized.input,
 335	 timestamp: sanitized.timestamp
 336	 });
 337	 
 338	 // Wrap trace for controlled access
 339	 const wrappedTrace = new WrappedTrace(
 340	 langfuseTrace,
 341	 this.contextManager,
 342	 this.sanitizer!
 343	 );
 344	 
 345	 // Create and set trace context
 346	 const context = this.contextManager.createContext(wrappedTrace, {
 347	 traceName: options.name,
 348	 startTime: Date.now()
 349	 });
 350	 this.contextManager.setContext(context);
 351	 
 352	 this.stats.tracesCreated++;
 353	 console.log(`✓ Started trace ${wrappedTrace.id} (total traces: ${this.stats.tracesCreated})`);
 354	 
 355	 return wrappedTrace;
 356	 
 357	 } catch (error) {
 358	 this.stats.errorsEncountered++;
 359	 console.error(`Failed to start trace '${options.name}':`, error);
 360	 
 361	 // Return no-op trace to prevent breaking the application
 362	 return new NoOpTrace();
 363	 }
 364	 }
