 31	export interface ILangfuseModule {
 32	 name: string;
 33	 version: string;
 34	 
 35	 // Lifecycle
 36	 initialize(config: LangfuseConfig): Promise<void>;
 37	 health(): Promise<HealthStatus>;
 38	 shutdown(): Promise<void>;
 39	 flush(): Promise<void>;
 40	 
 41	 // Tracing
 42	 startTrace(options: TraceOptions): ITrace;
 43	 getCurrentTrace(): ITrace | null;
 44	 
 45	 // Scoring
 46	 scoreTrace(traceId: string, score: Score): Promise<void>;
 47	 scoreGeneration(generationId: string, score: Score): Promise<void>;
 48	 
 49	 // Prompts
 50	 getPrompt(name: string, version?: string): Promise<IPrompt>;
 51	 
 52	 // Context management
 53	 runInTraceContext<T>(trace: ITrace, fn: () => Promise<T>): Promise<T>;
 54	 runInSpanContext<T>(span: ISpan, fn: () => Promise<T>): Promise<T>;
 55	}
 56	
 57	export interface LangfuseConfig {
 58	 publicKey: string;
 59	 secretKey: string;
 60	 baseUrl?: string;
 61	 
 62	 // Behavior options
 63	 enabled?: boolean; // Enable/disable tracing
 64	 flushInterval?: number; // Auto-flush interval
 65	 flushAt?: number; // Batch size for flushing
 66	 
 67	 // Privacy options
 68	 maskSensitiveData?: boolean;
 69	 redactPatterns?: RegExp[];
 70	 allowedMetadataKeys?: string[];
 71	 
 72	 // Performance options
 73	 samplingRate?: number; // 0-1, percentage of traces to capture
 74	 asyncFlush?: boolean;
 75	 timeout?: number;
 76	}
 77	
 78	export interface TraceOptions {
 79	 name: string;
 80	 id?: string; // Custom trace ID
 81	 userId?: string;
 82	 sessionId?: string;
 83	 metadata?: Record<string, any>;
 84	 tags?: string[];
 85	 release?: string;
 86	 version?: string;
 87	}
