 413	/**
 414	 * Error thrown when cost tracking and analytics fail.
 415	 * 
 416	 * Covers cost calculation errors, budget limit violations,
 417	 * analytics query failures, and reporting issues.
 418	 * 
 419	 * @example
 420	 * ```typescript
 421	 * throw new LangfuseCostError(
 422	 * 'budget_exceeded',
 423	 * 'Daily budget limit exceeded',
 424	 * {
 425	 * budgetType: 'daily',
 426	 * limit: 100,
 427	 * current: 105.50,
 428	 * currency: 'USD'
 429	 * }
 430	 * );
 431	 * ```
 432	 */
 433	export class LangfuseCostError extends LangfuseError {
 434	 /** Cost context information */
 435	 public readonly costContext: {
 436	 budgetType?: string;
 437	 limit?: number;
 438	 current?: number;
 439	 currency?: string;
 440	 model?: string;
 441	 provider?: string;
 442	 };
 443	 
 444	 constructor(
 445	 operation: 'cost_calculation' | 'budget_exceeded' | 'analytics_query' | 'cost_reporting',
 446	 message: string,
 447	 costContext: {
 448	 budgetType?: string;
 449	 limit?: number;
 450	 current?: number;
 451	 currency?: string;
 452	 model?: string;
 453	 provider?: string;
 454	 },
 455	 cause?: Error,
 456	 context: Record<string, any> = {}
 457	 ) {
 458	 let solution = 'Review cost configuration and usage patterns.';
 459	 let severity: 'low' | 'medium' | 'high' | 'critical' = 'medium';
 460	 
 461	 switch (operation) {
 462	 case 'cost_calculation':
 463	 solution = 'Verify model pricing configuration and token count accuracy.';
 464	 break;
 465	 case 'budget_exceeded':
 466	 solution = 'Increase budget limits or optimize model usage to reduce costs.';
 467	 severity = 'high';
 468	 break;
 469	 case 'analytics_query':
 470	 solution = 'Check query parameters and ensure sufficient data exists for the time range.';
 471	 break;
 472	 case 'cost_reporting':
 473	 solution = 'Verify reporting configuration and data availability.';
 474	 break;
 475	 }
 476	 
 477	 super(
 478	 'LANGFUSE_COST_ERROR',
 479	 `Cost operation failed (${operation}): ${message}`,
 480	 {
 481	 operation,
 482	 ...costContext,
 483	 ...context
 484	 },
 485	 solution,
 486	 cause,
 487	 operation !== 'budget_exceeded', // Budget violations aren't retryable
 488	 operation === 'budget_exceeded' ? undefined : 3000,
 489	 severity
 490	 );
 491	 
 492	 this.costContext = costContext;
 493	 }
 494	}
