 345	 * );
 346	 * ```
 347	 */
 348	export class LangfusePromptError extends LangfuseError {
 349	 /** Prompt context information */
 350	 public readonly promptContext: {
 351	 promptName?: string;
 352	 version?: number | 'latest';
 353	 variables?: Record<string, any>;
 354	 template?: string;
 355	 };
 356	 
 357	 constructor(
 358	 operation: 'prompt_creation' | 'prompt_retrieval' | 'prompt_compilation' | 'prompt_versioning' | 'template_rendering',
 359	 message: string,
 360	 promptContext: {
 361	 promptName?: string;
 362	 version?: number | 'latest';
 363	 variables?: Record<string, any>;
 364	 template?: string;
 365	 },
 366	 cause?: Error,
 367	 context: Record<string, any> = {}
 368	 ) {
 369	 let solution = 'Review prompt configuration and template syntax.';
 370	 let severity: 'low' | 'medium' | 'high' | 'critical' = 'medium';
 371	 
 372	 switch (operation) {
