 96	 * 
 97	 * // Start trace - context is automatic
 98	 * const trace = langfuse.startTrace({ name: 'workflow' });
 99	 * 
 100	 * // All operations in this context are traced
 101	 * await langfuse.runInTraceContext(trace, async () => {
 102	 * // Nested operations automatically get proper context
 103	 * await agent.execute(input);
 104	 * });
 105	 * 
 106	 * // Clean shutdown
 107	 * await langfuse.shutdown();
