 194	 
 195	 console.log(`Registering module: ${moduleId} (${module.name} v${module.version})`);
 196	 
 197	 try {
 198	 // Add to dependency graph (this validates dependencies and detects cycles)
 199	 this.dependencyGraph.addNode(moduleId, module.dependencies || []);
 200	 
 201	 // Store module metadata
 202	 const metadata: ModuleMetadata = {
 203	 id: moduleId,
 204	 module,
 205	 status: 'registered',
 206	 initialized: false,
 207	 config: this.config?.modules?.[moduleId] // Will be null until initialize() is called
 208	 };
 209	 
 210	 this.modules.set(moduleId, metadata);
 211	 
 212	 console.log(`✓ Module ${moduleId} registered successfully`);
 213	 
 214	 } catch (error) {
 215	 // Clean up on failure
 216	 this.dependencyGraph.removeNode(moduleId);
 217	 throw error; // Re-throw the original error (likely CircularDependencyError)
 218	 }
