 445	 console.log(`Retrieving session '${sessionId}'`);
 446	 
 447	 // Check cache first unless refresh is requested
 448	 if (!options.refresh) {
 449	 const cachedSession = this.getFromCache(sessionId);
 450	 if (cachedSession) {
 451	 this.stats.cacheHits++;
 452	 console.log(`✓ Retrieved session '${sessionId}' from cache`);
 453	 return cachedSession;
 454	 }
 455	 }
 456	 
 457	 this.stats.cacheMisses++;
 458	 
 459	 // Load from storage
 460	 const session = await this.storage!.loadSession(sessionId);
 461	 
 462	 if (!session) {
 463	 console.log(`Session '${sessionId}' not found`);
 464	 return null;
 465	 }
 466	 
 467	 // Add to cache
 468	 this.addToCache(session);
 469	 
 470	 // Update statistics
 471	 this.stats.sessionsLoaded++;
 472	 
 473	 console.log(`✓ Retrieved session '${sessionId}' from storage`);
 474	 
 475	 return session;
 476	 
 477	 } catch (error) {
 478	 this.stats.errorsEncountered++;
 479	 
 480	 throw new SessionRetrievalError(
 481	 sessionId,
 482	 error instanceof Error ? error.message : String(error),
 483	 error instanceof Error ? error : undefined,
 484	 createSessionErrorContext({ id: sessionId }, 'retrieve', { options })
 485	 );
