 75	 registry: {
 76	 strict?: boolean; // Fail on missing dependencies
 77	 lazyLoad?: boolean; // Initialize modules on first use
 78	 healthCheckInterval?: number; // Health check interval in ms
 79	 shutdownTimeout?: number; // Graceful shutdown timeout
