 338	 // Set a reasonable timeout for health checks
 339	 const healthCheckTimeout = 5000; // 5 seconds
 340	 
 341	 try {
 342	 // Create a timeout promise
 343	 const timeoutPromise = new Promise<never>((_, reject) => {
 344	 setTimeout(() => reject(new Error('Health check timeout')), healthCheckTimeout);
 345	 });
 346	 
 347	 // Race the health check against the timeout
 348	 const healthResult = await Promise.race([
 349	 metadata.module.health(),
 350	 timeoutPromise
 351	 ]);
 352	 
 353	 return healthResult;
 354	 
 355	 } catch (error) {
 356	 // Convert any error into an unhealthy status
 357	 return {
 358	 status: 'unhealthy',
 359	 message: `Health check error: ${error.message}`,
 360	 details: {
 361	 error: error.name,
 362	 moduleId,
 363	 timestamp: new Date().toISOString()
 364	 }
 365	 };
