 40	/**
 41	 * Creates a new Module Registry instance with sensible defaults.
 42	 * 
 43	 * This factory function creates a registry and pre-registers all core
 44	 * AgentHub modules using factory functions. This enables lazy loading
 45	 * and proper dependency injection.
 46	 * 
 47	 * @param config - Optional registry configuration (merged with defaults)
 48	 * @returns A new Module Registry instance with core modules registered
 49	 * 
 50	 * @example
 51	 * ```typescript
 52	 * const registry = createModuleRegistry({
 53	 * modules: {
 54	 * sessionState: {
 55	 * enabled: true,
 56	 * config: { storage: 'redis', ttl: 3600 }
 57	 * },
 58	 * memory: {
 59	 * enabled: true,
 60	 * config: { vectorStore: 'pinecone' }
 61	 * }
 62	 * },
 63	 * registry: {
 64	 * strict: true,
 65	 * healthCheckInterval: 30000
 66	 * }
 67	 * });
 68	 * 
 69	 * await registry.initialize(config);
 70	 * ```
 71	 * 
 72	 * Implementation notes:
 73	 * - Registers factories for all core modules
 74	 * - Uses lazy loading to avoid circular dependencies
 75	 * - Provides environment-specific defaults
 76	 * - Factories handle dependency injection automatically
 77	 */
 78	export function createModuleRegistry(
 79	 config?: Partial<ModuleRegistryConfig>
 80	): IModuleRegistry {
 81	 const registry = new ModuleRegistry();
 82	 
 83	 // Build complete configuration with defaults
 84	 const fullConfig = buildDefaultConfig(config);
 85	 
 86	 // Pre-register all core module factories
 87	 // These will be created lazily when first accessed or during initialization
 88	 registerCoreModules(registry);
 89	 
 90	 return registry;
 91	}
