 31	export interface IModuleRegistry {
 32	 // Registration
 33	 register(moduleId: string, module: IModule): void;
 34	 registerFactory(moduleId: string, factory: ModuleFactory): void;
 35	 
 36	 // Access
 37	 get<T extends IModule>(moduleId: string): T;
 38	 getAll(): Map<string, IModule>;
 39	 has(moduleId: string): boolean;
 40	 
 41	 // Lifecycle
 42	 initialize(config: ModuleRegistryConfig): Promise<void>;
 43	 shutdown(): Promise<void>;
 44	 reload(moduleId: string): Promise<void>;
 45	 
 46	 // Health
 47	 health(): Promise<RegistryHealth>;
 48	 getModuleHealth(moduleId: string): Promise<HealthStatus>;
 49	 
 50	 // Dependencies
 51	 getDependencies(moduleId: string): string[];
 52	 getDependents(moduleId: string): string[];
 53	 getInitializationOrder(): string[];
 54	}
