 56	export interface IModule {
 57	 name: string;
 58	 version: string;
 59	 
 60	 // Lifecycle
 61	 initialize?(config: any): Promise<void>;
 62	 shutdown?(): Promise<void>;
 63	 health?(): Promise<HealthStatus>;
 64	 
 65	 // Dependencies
 66	 dependencies?: string[];
 67	 optionalDependencies?: string[];
 68	}
 69	
 70	export interface ModuleRegistryConfig {
 71	 // Module configurations
 72	 modules: Record<string, ModuleConfig>;
 73	 
 74	 // Registry settings
 75	 registry: {
 76	 strict?: boolean; // Fail on missing dependencies
 77	 lazyLoad?: boolean; // Initialize modules on first use
 78	 healthCheckInterval?: number; // Health check interval in ms
 79	 shutdownTimeout?: number; // Graceful shutdown timeout
