 300	### Step 3: Module Wrapper
 301	Create `modules/registry/wrapper.ts`:
 302	
 303	```typescript
 304	export function wrapModule(
 305	 module: IModule,
 306	 options: WrapperOptions
 307	): IModule {
 308	 return new Proxy(module, {
 309	 get(target, prop, receiver) {
 310	 const value = Reflect.get(target, prop, receiver);
 311	 
 312	 // Wrap methods for monitoring
 313	 if (typeof value === 'function' && options.monitoring?.trackMethodCalls?.includes(prop as string)) {
 314	 return wrapMethod(value, target, prop as string, options);
 315	 }
 316	 
 317	 return value;
 318	 }
 319	 });
 320	}
 321	
 322	function wrapMethod(
 323	 method: Function,
 324	 module: IModule,
 325	 methodName: string,
 326	 options: WrapperOptions
 327	): Function {
 328	 return async function(...args: any[]) {
 329	 const startTime = Date.now();
 330	 const correlationId = generateId('corr');
 331	 
 332	 // Pre-execution hooks
 333	 if (options.monitoring?.onMethodCall) {
 334	 options.monitoring.onMethodCall({
 335	 module: module.name,
 336	 method: methodName,
 337	 args,
 338	 correlationId,
 339	 timestamp: new Date()
 340	 });
 341	 }
 342	 
 343	 try {
 344	 // Check access control
 345	 if (options.access?.validator) {
 346	 const allowed = await options.access.validator({
 347	 module: module.name,
 348	 method: methodName,
 349	 args
 350	 });
 351	 
 352	 if (!allowed) {
 353	 throw new Error(`Access denied to ${module.name}.${methodName}`);
 354	 }
 355	 }
 356	 
 357	 // Apply rate limiting
 358	 if (options.access?.rateLimit) {
 359	 const limiter = getRateLimiter(module.name, methodName, options.access.rateLimit);
 360	 await limiter.check();
 361	 }
 362	 
 363	 // Execute method
 364	 const result = await method.apply(module, args);
 365	 
 366	 // Track success
 367	 if (options.monitoring?.trackLatency) {
 368	 trackLatency(module.name, methodName, Date.now() - startTime);
 369	 }
 370	 
 371	 return result;
 372	 
 373	 } catch (error) {
 374	 // Track error
 375	 if (options.monitoring?.trackErrors) {
 376	 trackError(module.name, methodName, error);
 377	 }
 378	 
 379	 // Error handling
 380	 if (options.errorHandling?.transform) {
 381	 throw options.errorHandling.transform(error);
 382	 }
 383	 
 384	 if (options.errorHandling?.swallowErrors) {
 385	 console.error(`Error in ${module.name}.${methodName}:`, error);
 386	 return null;
 387	 }
 388	 
 389	 throw error;
 390	 
 391	 } finally {
 392	 // Post-execution hooks
 393	 if (options.monitoring?.onMethodComplete) {
 394	 options.monitoring.onMethodComplete({
 395	 module: module.name,
 396	 method: methodName,
 397	 duration: Date.now() - startTime,
 398	 correlationId
 399	 });
 400	 }
 401	 }
 402	 };
 403	}
 404	
 405	export interface WrapperOptions {
 406	 name: string;
 407	 
 408	 monitoring?: {
 409	 trackLatency?: boolean;
 410	 trackErrors?: boolean;
 411	 trackMethodCalls?: string[];
 412	 onMethodCall?: (info: MethodCallInfo) => void;
 413	 onMethodComplete?: (info: MethodCompleteInfo) => void;
 414	 };
 415	 
 416	 access?: {
 417	 validator?: (context: AccessContext) => boolean | Promise<boolean>;
 418	 rateLimit?: RateLimitConfig;
 419	 };
 420	 
 421	 errorHandling?: {
 422	 transform?: (error: Error) => Error;
 423	 swallowErrors?: boolean;
 424	 logErrors?: boolean;
 425	 };
 426	 
 427	 caching?: {
 428	 methods?: string[];
 429	 ttl?: number;
 430	 keyGenerator?: (args: any[]) => string;
 431	 };
 432	}
 433	```
 434	
 435	### Step 4: Module Registry Implementation
 436	Create `modules/registry/index.ts`:
 437	
 438	```typescript
 439	export class ModuleRegistry implements IModuleRegistry {
 440	 private modules = new Map<string, ModuleMetadata>();
 441	 private dependencyGraph = new DependencyGraph();
 442	 private config: ModuleRegistryConfig;
 443	 private healthChecker?: HealthChecker;
 444	 private initializationPromise?: Promise<void>;
 445	 
 446	 register(moduleId: string, module: IModule): void {
 447	 if (this.modules.has(moduleId)) {
 448	 throw new Error(`Module ${moduleId} already registered`);
 449	 }
 450	 
 451	 // Add to dependency graph
 452	 this.dependencyGraph.addNode(
 453	 moduleId,
 454	 module.dependencies || []
 455	 );
 456	 
 457	 // Check for circular dependencies
 458	 const cycles = this.dependencyGraph.detectCircularDependencies();
 459	 if (cycles.length > 0) {
 460	 this.dependencyGraph.removeNode(moduleId);
 461	 throw new Error(
 462	 `Circular dependency detected: ${cycles[0].join(' -> ')}`
 463	 );
 464	 }
 465	 
 466	 // Store module metadata
 467	 this.modules.set(moduleId, {
 468	 id: moduleId,
 469	 module,
 470	 status: 'registered',
 471	 initialized: false
 472	 });
 473	 }
 474	 
 475	 registerFactory(moduleId: string, factory: ModuleFactory): void {
 476	 if (this.modules.has(moduleId)) {
 477	 throw new Error(`Module ${moduleId} already registered`);
 478	 }
 479	 
 480	 // Store factory for lazy initialization
 481	 this.modules.set(moduleId, {
 482	 id: moduleId,
 483	 module: null as any, // Will be created by factory
 484	 factory,
 485	 status: 'registered',
 486	 initialized: false
 487	 });
 488	 }
 489	 
 490	 get<T extends IModule>(moduleId: string): T {
 491	 const metadata = this.modules.get(moduleId);
 492	 
 493	 if (!metadata) {
 494	 throw new Error(`Module ${moduleId} not found`);
 495	 }
 496	 
 497	 // Lazy initialization if configured
 498	 if (this.config?.registry?.lazyLoad && !metadata.initialized) {
 499	 this.initializeModule(moduleId).catch(error => {
 500	 console.error(`Failed to lazy-initialize module ${moduleId}:`, error);
 501	 throw error;
 502	 });
 503	 }
 504	 
 505	 if (metadata.status === 'error') {
 506	 throw new Error(
 507	 `Module ${moduleId} is in error state: ${metadata.error?.message}`
 508	 );
 509	 }
 510	 
 511	 if (!metadata.initialized) {
 512	 throw new Error(`Module ${moduleId} not initialized`);
 513	 }
 514	 
 515	 return metadata.module as T;
 516	 }
 517	 
 518	 async initialize(config: ModuleRegistryConfig): Promise<void> {
 519	 if (this.initializationPromise) {
 520	 return this.initializationPromise;
 521	 }
 522	 
 523	 this.config = config;
 524	 
 525	 this.initializationPromise = this.doInitialize();
 526	 return this.initializationPromise;
 527	 }
 528	 
 529	 private async doInitialize(): Promise<void> {
 530	 // Validate all dependencies exist
 531	 if (this.config.registry?.strict) {
 532	 this.validateDependencies();
 533	 }
 534	 
 535	 // Get initialization order
 536	 const initOrder = this.dependencyGraph.getInitializationOrder();
 537	 
 538	 // Initialize modules in order
 539	 for (const moduleId of initOrder) {
 540	 if (this.modules.has(moduleId)) {
 541	 await this.initializeModule(moduleId);
 542	 }
 543	 }
 544	 
 545	 // Start health monitoring
 546	 if (this.config.registry?.healthCheckInterval) {
 547	 this.healthChecker = new HealthChecker(
 548	 this,
 549	 this.config.registry.healthCheckInterval
 550	 );
 551	 this.healthChecker.start();
 552	 }
 553	 
 554	 // Setup hot reload in development
 555	 if (this.config.development?.hotReload) {
 556	 this.setupHotReload();
 557	 }
 558	 }
 559	 
 560	 private async initializeModule(moduleId: string): Promise<void> {
 561	 const metadata = this.modules.get(moduleId)!;
 562	 
 563	 if (metadata.initialized) {
 564	 return;
 565	 }
 566	 
 567	 metadata.status = 'initializing';
 568	 const startTime = Date.now();
 569	 
 570	 try {
 571	 // Create module from factory if needed
 572	 if (metadata.factory && !metadata.module) {
 573	 metadata.module = await metadata.factory(this);
 574	 
 575	 // Update dependency graph with actual dependencies
 576	 this.dependencyGraph.removeNode(moduleId);
 577	 this.dependencyGraph.addNode(
 578	 moduleId,
 579	 metadata.module.dependencies || []
 580	 );
 581	 }
 582	 
 583	 // Get module config
 584	 const moduleConfig = this.config.modules?.[moduleId];
 585	 
 586	 // Skip if disabled
 587	 if (moduleConfig?.enabled === false) {
 588	 metadata.status = 'stopped';
 589	 return;
 590	 }
 591	 
 592	 // Initialize with timeout
 593	 if (metadata.module.initialize) {
 594	 const timeout = moduleConfig?.initTimeout || 30000;
 595	 
 596	 await this.withTimeout(
 597	 metadata.module.initialize(moduleConfig?.config),
 598	 timeout,
 599	 `Module ${moduleId} initialization timed out`
 600	 );
 601	 }
 602	 
 603	 metadata.initialized = true;
 604	 metadata.status = 'ready';
 605	 metadata.initializeTime = Date.now() - startTime;
 606	 
 607	 console.log(
 608	 `Module ${moduleId} initialized in ${metadata.initializeTime}ms`
 609	 );
 610	 
 611	 } catch (error) {
 612	 metadata.status = 'error';
 613	 metadata.error = error;
 614	 
 615	 if (this.config.registry?.strict) {
 616	 throw new Error(
 617	 `Failed to initialize module ${moduleId}: ${error.message}`
 618	 );
 619	 }
 620	 
 621	 console.error(`Failed to initialize module ${moduleId}:`, error);
 622	 }
 623	 }
 624	 
 625	 async shutdown(): Promise<void> {
 626	 // Stop health checker
 627	 if (this.healthChecker) {
 628	 this.healthChecker.stop();
 629	 }
 630	 
 631	 // Get shutdown order (reverse of initialization)
 632	 const shutdownOrder = this.dependencyGraph
 633	 .getInitializationOrder()
 634	 .reverse();
 635	 
 636	 // Shutdown modules in order
 637	 const timeout = this.config.registry?.shutdownTimeout || 30000;
 638	 const shutdownPromises = [];
 639	 
 640	 for (const moduleId of shutdownOrder) {
 641	 const metadata = this.modules.get(moduleId);
 642	 
 643	 if (metadata?.initialized && metadata.module.shutdown) {
 644	 metadata.status = 'stopping';
 645	 
 646	 const shutdownPromise = this.withTimeout(
 647	 metadata.module.shutdown(),
 648	 timeout,
 649	 `Module ${moduleId} shutdown timed out`
 650	 ).then(() => {
 651	 metadata.status = 'stopped';
 652	 metadata.initialized = false;
 653	 }).catch(error => {
 654	 console.error(`Error shutting down module ${moduleId}:`, error);
 655	 metadata.status = 'error';
 656	 metadata.error = error;
 657	 });
 658	 
 659	 shutdownPromises.push(shutdownPromise);
 660	 }
 661	 }
 662	 
 663	 await Promise.all(shutdownPromises);
 664	 }
 665	 
 666	 async reload(moduleId: string): Promise<void> {
 667	 const metadata = this.modules.get(moduleId);
 668	 if (!metadata) {
 669	 throw new Error(`Module ${moduleId} not found`);
 670	 }
 671	 
 672	 // Get dependent modules
 673	 const dependents = this.dependencyGraph.getDependents(moduleId);
 674	 
 675	 // Shutdown module and dependents
 676	 const shutdownOrder = [moduleId, ...dependents].reverse();
 677	 for (const id of shutdownOrder) {
 678	 const meta = this.modules.get(id);
 679	 if (meta?.module.shutdown) {
 680	 await meta.module.shutdown();
 681	 }
 682	 }
 683	 
 684	 // Reinitialize module and dependents
 685	 await this.initializeModule(moduleId);
 686	 for (const dependentId of dependents) {
 687	 await this.initializeModule(dependentId);
 688	 }
 689	 }
 690	 
 691	 async health(): Promise<RegistryHealth> {
 692	 const moduleHealths: Record<string, ModuleHealthInfo> = {};
 693	 
 694	 for (const [moduleId, metadata] of this.modules.entries()) {
 695	 const healthInfo: ModuleHealthInfo = {
 696	 status: metadata.status,
 697	 lastCheck: metadata.lastHealthCheck
 698	 };
 699	 
 700	 if (metadata.initialized && metadata.module.health) {
 701	 try {
 702	 const health = await metadata.module.health();
 703	 healthInfo.health = health;
 704	 metadata.healthStatus = health;
 705	 } catch (error) {
 706	 healthInfo.health = {
 707	 status: 'unhealthy',
 708	 message: error.message
 709	 };
 710	 }
 711	 }
 712	 
 713	 if (metadata.initializeTime) {
 714	 healthInfo.uptime = Date.now() - metadata.initializeTime;
 715	 }
 716	 
 717	 moduleHealths[moduleId] = healthInfo;
 718	 }
 719	 
 720	 // Calculate summary
 721	 const summary = {
 722	 total: this.modules.size,
 723	 healthy: 0,
 724	 degraded: 0,
 725	 unhealthy: 0
 726	 };
 727	 
 728	 for (const health of Object.values(moduleHealths)) {
 729	 if (health.health?.status === 'healthy') {
 730	 summary.healthy++;
 731	 } else if (health.health?.status === 'degraded') {
 732	 summary.degraded++;
 733	 } else {
 734	 summary.unhealthy++;
 735	 }
 736	 }
 737	 
 738	 // Determine overall status
 739	 let status: 'healthy' | 'degraded' | 'unhealthy';
 740	 if (summary.unhealthy > 0) {
 741	 status = 'unhealthy';
 742	 } else if (summary.degraded > 0) {
 743	 status = 'degraded';
 744	 } else {
 745	 status = 'healthy';
 746	 }
 747	 
 748	 return {
 749	 status,
 750	 modules: moduleHealths,
 751	 summary
 752	 };
 753	 }
 754	 
 755	 private validateDependencies(): void {
 756	 for (const [moduleId, metadata] of this.modules.entries()) {
 757	 const dependencies = metadata.module?.dependencies || [];
 758	 
 759	 for (const dep of dependencies) {
 760	 if (!this.modules.has(dep)) {
 761	 throw new Error(
 762	 `Module ${moduleId} depends on ${dep} which is not registered`
 763	 );
 764	 }
 765	 }
 766	 }
 767	 }
 768	 
 769	 private async withTimeout<T>(
 770	 promise: Promise<T>,
 771	 timeout: number,
 772	 errorMessage: string
 773	 ): Promise<T> {
 774	 const timeoutPromise = new Promise<never>((_, reject) => {
 775	 setTimeout(() => reject(new Error(errorMessage)), timeout);
 776	 });
 777	 
 778	 return Promise.race([promise, timeoutPromise]);
 779	 }
 780	}
 781	```
 782	
 783	### Step 5: Health Monitoring
 784	Create `modules/registry/health-checker.ts`:
 785	
 786	```typescript
 787	export class HealthChecker {
 788	 private interval?: NodeJS.Timer;
 789	 private checking = false;
 790	 
 791	 constructor(
 792	 private registry: ModuleRegistry,
 793	 private checkInterval: number
 794	 ) {}
 795	 
 796	 start(): void {
 797	 if (this.interval) {
 798	 return;
 799	 }
 800	 
 801	 this.interval = setInterval(async () => {
 802	 if (this.checking) {
 803	 return; // Skip if previous check still running
 804	 }
 805	 
 806	 this.checking = true;
 807	 
 808	 try {
 809	 await this.checkHealth();
 810	 } catch (error) {
 811	 console.error('Health check error:', error);
 812	 } finally {
 813	 this.checking = false;
 814	 }
 815	 }, this.checkInterval);
 816	 
 817	 // Run initial check
 818	 this.checkHealth().catch(console.error);
 819	 }
 820	 
 821	 stop(): void {
 822	 if (this.interval) {
 823	 clearInterval(this.interval);
 824	 this.interval = undefined;
 825	 }
 826	 }
 827	 
 828	 private async checkHealth(): Promise<void> {
 829	 const modules = this.registry.getAll();
 830	 
 831	 for (const [moduleId, metadata] of modules.entries()) {
 832	 if (metadata.initialized && metadata.module.health) {
 833	 try {
 834	 const health = await metadata.module.health();
 835	 
 836	 metadata.healthStatus = health;
 837	 metadata.lastHealthCheck = new Date();
 838	 
 839	 // Update status based on health
 840	 if (health.status === 'unhealthy') {
 841	 metadata.status = 'error';
 842	 } else if (metadata.status === 'error' && health.status === 'healthy') {
 843	 metadata.status = 'ready';
 844	 }
 845	 
 846	 } catch (error) {
 847	 metadata.healthStatus = {
 848	 status: 'unhealthy',
 849	 message: `Health check failed: ${error.message}`,
 850	 details: { error }
 851	 };
 852	 
 853	 console.error(`Health check failed for ${moduleId}:`, error);
 854	 }
 855	 }
 856	 }
 857	 
 858	 // Emit health status event
 859	 const overallHealth = await this.registry.health();
 860	 
 861	 if (overallHealth.status !== 'healthy') {
 862	 console.warn('Registry health degraded:', overallHealth);
 863	 }
 864	 }
 865	}
 866	```
