 868	### Step 6: Module Registry Factory
 869	Create `modules/registry/factory.ts`:
 870	
 871	```typescript
 872	export function createModuleRegistry(
 873	 config?: Partial<ModuleRegistryConfig>
 874	): IModuleRegistry {
 875	 const registry = new ModuleRegistry();
 876	 
 877	 // Register core modules with factories
 878	 registry.registerFactory('litellm', async (reg) => {
 879	 const { createLiteLLMModule } = await import('../litellm/factory');
 880	 return createLiteLLMModule(config?.modules?.litellm?.config || {});
 881	 });
 882	 
 883	 registry.registerFactory('langfuse', async (reg) => {
 884	 const { createLangfuseModule } = await import('../langfuse/factory');
 885	 return createLangfuseModule(config?.modules?.langfuse?.config || {});
 886	 });
 887	 
 888	 registry.registerFactory('sessionState', async (reg) => {
 889	 const { createSessionStateModule } = await import('../session-state/factory');
 890	 return createSessionStateModule(config?.modules?.sessionState?.config || {});
 891	 });
 892	 
 893	 registry.registerFactory('memory', async (reg) => {
 894	 const { createMemoryModule } = await import('../memory/factory');
 895	 return createMemoryModule(config?.modules?.memory?.config || {});
 896	 });
 897	 
 898	 registry.registerFactory('tools', async (reg) => {
 899	 const { createToolsModule } = await import('../tools/factory');
 900	 return createToolsModule(config?.modules?.tools?.config || {});
 901	 });
 902	 
 903	 return registry;
 904	}
 905	
 906	// Singleton instance for global access
 907	let globalRegistry: IModuleRegistry | null = null;
 908	
 909	export function getGlobalRegistry(): IModuleRegistry {
 910	 if (!globalRegistry) {
 911	 globalRegistry = createModuleRegistry();
 912	 }
 913	 return globalRegistry;
 914	}
 915	
 916	export function setGlobalRegistry(registry: IModuleRegistry): void {
 917	 globalRegistry = registry;
 918	}
 919	```
