 275	 
 276	 // Calculate overall health metrics
 277	 const duration = Date.now() - startTime;
 278	 const healthSummary = this.calculateHealthSummary(moduleHealthResults);
 279	 
 280	 // Record this health check
 281	 this.recordHealthCheck({
 282	 timestamp: new Date(),
 283	 duration,
 284	 moduleResults: moduleHealthResults,
 285	 summary: healthSummary,
 286	 success: true
 287	 });
 288	 
 289	 // Log health status changes
 290	 this.logHealthChanges(moduleHealthResults);
 291	 
 292	 // Reset failure counter on successful check
 293	 this.consecutiveFailures = 0;
 294	 this.lastSuccessfulCheck = new Date();
 295	 
 296	 console.debug(`Health check completed in ${duration}ms - ${healthSummary.healthy}/${healthSummary.total} modules healthy`);
 297	 
 298	 } catch (error) {
 299	 console.error('Health check failed:', error);
 300	 
 301	 // Record failed health check
 302	 this.recordHealthCheck({
 303	 timestamp: new Date(),
 304	 duration: Date.now() - startTime,
 305	 moduleResults: {},
 306	 summary: { total: 0, healthy: 0, degraded: 0, unhealthy: 0 },
 307	 success: false,
 308	 error: error.message
 309	 });
 310	 
 311	 this.consecutiveFailures++;
 312	 
 313	 // Implement exponential backoff for repeated failures
 314	 if (this.consecutiveFailures >= 3) {
 315	 console.warn(`Health checker has failed ${this.consecutiveFailures} consecutive times`);
 316	 }
 317	 
 318	 } finally {
 319	 this.checking = false;
 320	 }
 321	 }
...
