 54	/**
 55	 * Manages dependency relationships between modules and determines initialization order.
 56	 * 
 57	 * The DependencyGraph maintains a directed acyclic graph (DAG) of module dependencies
 58	 * and provides methods to:
 59	 * - Add and remove modules with their dependencies
 60	 * - Detect circular dependencies
 61	 * - Determine proper initialization order using topological sort
 62	 * - Query dependency relationships
 63	 * 
 64	 * @remarks
 65	 * This class is designed to be efficient with O(V+E) complexity for most operations
 66	 * where V is the number of modules and E is the number of dependency edges.
 67	 * 
 68	 * @example
 69	 * ```typescript
 70	 * const graph = new DependencyGraph();
 71	 * 
 72	 * // Build dependency graph
 73	 * graph.addNode('sessionState', []);
 74	 * graph.addNode('memory', ['sessionState']); 
...
 90	 
 91	 /**
 92	 * Adds a module node to the dependency graph.
 93	 * 
 94	 * If the module already exists, its dependencies are updated.
 95	 * This method automatically creates nodes for any referenced dependencies
 96	 * and maintains bidirectional relationships.
 97	 * 
 98	 * @param moduleId - Unique identifier for the module
 99	 * @param dependencies - Array of module IDs this module depends on
 100	 * 
 101	 * @throws {CircularDependencyError} If adding this node creates a circular dependency
 102	 * 
 103	 * @example
 104	 * ```typescript
 105	 * graph.addNode('llmAgent', ['memory', 'sessionState']);
 106	 * // Creates nodes for 'memory' and 'sessionState' if they don't exist
 107	 * ```
 108	 * 
 109	 * Implementation notes:
 110	 * - Creates dependency nodes automatically if they don't exist
 111	 * - Updates bidirectional relationships (dependencies and dependents)
 112	 * - Validates that no circular dependencies are created
 113	 * - O(D) complexity where D is the number of dependencies
 114	 */
 115	 addNode(moduleId: string, dependencies: string[] = []): void {
 116	 // Create or get the main node
 117	 if (!this.nodes.has(moduleId)) {
 118	 this.nodes.set(moduleId, {
 119	 id: moduleId,
 120	 dependencies: new Set(),
 121	 dependents: new Set()
 122	 });
 123	 }
 124	 
 125	 const node = this.nodes.get(moduleId)!;
 126	 
 127	 // Clear existing dependencies and rebuild
 128	 // This is necessary when updating an existing node's dependencies
 129	 for (const existingDep of node.dependencies) {
 130	 const depNode = this.nodes.get(existingDep);
 131	 if (depNode) {
 132	 depNode.dependents.delete(moduleId);
 133	 }
 134	 }
 135	 node.dependencies.clear();
 136	 
 137	 // Add new dependencies
 138	 for (const dep of dependencies) {
 139	 // Skip self-dependencies (a module can't depend on itself)
 140	 if (dep === moduleId) {
 141	 console.warn(`Module '${moduleId}' cannot depend on itself, skipping self-dependency`);
 142	 continue;
 143	 }
 144	 
 145	 node.dependencies.add(dep);
 146	 
 147	 // Create dependency node if it doesn't exist
 148	 if (!this.nodes.has(dep)) {
 149	 this.nodes.set(dep, {
 150	 id: dep,
 151	 dependencies: new Set(),
 152	 dependents: new Set()
 153	 });
 154	 }
 155	 
 156	 // Add reverse dependency (bidirectional relationship)
 157	 this.nodes.get(dep)!.dependents.add(moduleId);
 158	 }
