 867	 async register(tool: ITool): Promise<void> {
 868	 this.ensureInitialized();
 869	 
 870	 // Check security
 871	 if (!this.isToolAllowed(tool.name)) {
 872	 throw new ToolError(
 873	 'SECURITY_ERROR',
 874	 `Tool ${tool.name} is not allowed`
 875	 );
 876	 }
 877	 
 878	 await this.registry.register(tool);
 879	 
 880	 // Store in persistent storage
 881	 await this.storage.saveTool(tool);
 882	 }
 883	 
 884	 async execute(
 885	 toolName: string,
 886	 args: any,
 887	 context?: ToolContext
 888	 ): Promise<ToolResult> {
 889	 this.ensureInitialized();
 890	 
 891	 // Add trace context
 892	 const enrichedContext = this.enrichContext(context);
 893	 
 894	 // Execute
 895	 const result = await this.executor.execute(
 896	 toolName,
 897	 args,
 898	 enrichedContext
 899	 );
 900	 
 901	 // Store execution history
 902	 await this.storage.saveExecution({
 903	 toolName,
 904	 args,
 905	 result,
 906	 context: enrichedContext,
 907	 timestamp: new Date()
 908	 });
 909	 
 910	 return result;
 911	 }
