 411	 /**
 412	 * Core workflow execution logic with dependency management.
 413	 * 
 414	 * Implements the main execution loop that processes nodes according to
 415	 * their dependencies, manages execution state, handles errors, and
 416	 * coordinates with streaming and persistence systems.
 417	 * 
 418	 * Execution algorithm:
 419	 * 1. Initialize execution queue with start nodes (no dependencies)
 420	 * 2. Process execution queue until empty or execution terminated
 421	 * 3. For each node: validate dependencies, execute, update state
 422	 * 4. Handle node completion: update waiting nodes, queue ready nodes
 423	 * 5. Manage parallel execution within configured limits
 424	 * 6. Handle errors with retry policies and recovery mechanisms
 425	 * 
 426	 * @param context - Execution context with state and configuration
...
 449	 
 450	 // Step 2: Main execution loop - process nodes until completion or failure
 451	 while (state.executionQueue.length > 0 && state.status === 'RUNNING') {
 452	 const nodeId = state.executionQueue.shift()!;
 453	 
 454	 try {
 455	 // Execute individual node with comprehensive error handling
 456	 await this.executeNode(context, nodeId);
 457	 
 458	 // Stream progress update if streaming enabled
 459	 if (streamer) {
 460	 const progress = this.calculateProgress(state, workflow.nodes.length);
 461	 streamer.streamWorkflowProgress(
 462	 context.input.sessionId,
 463	 workflow.id,
 464	 progress
 465	 );
 466	 }
 467	 
 468	 // Check waiting nodes for readiness after this node completion
 469	 await this.checkWaitingNodes(context);
 470	 
 471	 } catch (error) {
 472	 // Handle node execution error with retry and recovery logic
 473	 await this.handleNodeError(context, nodeId, error);
 474	 }
