 638	 nodeId: node.id,
 639	 workflowId: context.workflow.id,
 640	 timestamp: new Date().toISOString(),
 641	 retryCount: 0 // Would be incremented for retries
 642	 }
 643	 };
 644	 
 645	 // Step 3: Execute agent with timeout protection
 646	 const timeout = node.timeout || this.config.defaultTimeout || 300000; // 5 minutes default
 647	 
 648	 return Promise.race([
 649	 agent.execute(agentInput),
