 346	 const batchSize = this.config?.performance?.maxFilesPerBatch || 100;
 347	 const fileBatches = this.createBatches(files, batchSize);
 348	
 349	 const codeAnalysisPromises = fileBatches.map((batch, index) =>
 350	 this.enhancedQueueService.submitCodeAnalysisJob({
