 426	 /**
 427	 * Create batches from array
 428	 */
 429	 private createBatches<T>(items: T[], batchSize: number): T[][] {
 430	 const batches: T[][] = [];
 431	 for (let i = 0; i < items.length; i += batchSize) {
 432	 batches.push(items.slice(i, i + batchSize));
 433	 }
 434	 return batches;
 435	 }
