 516	 }
 517	 }
 518	
 519	 /**
 520	 * Emit sync status update via WebSocket
 521	 */
 522	 private async emitSyncUpdate(
 523	 syncJobId: string,
 524	 codebaseId: string,
 525	 update: {
 526	 status: string;
 527	 progress: number;
 528	 message?: string;
 529	 error?: string;
 530	 },
 531	 ): Promise<void> {
 532	 try {
 533	 this.webSocketService.emitToRoom(`codebase:${codebaseId}`, 'syncUpdate', {
 534	 syncJobId,
 535	 codebaseId,
 536	 ...update,
 537	 timestamp: new Date(),
 538	 });
 539	 } catch (error) {
 540	 this.logger.warn('Failed to emit sync update:', error);
 541	 }
 542	 }
