 57	 /**
 58	 * Main orchestrator - Coordinates parallel pipelines (codebase-indexer approach)
 59	 */
 60	 @Processor(JOB_TYPES.SYNC_CODEBASE)
 61	 async handleSyncCodebase(job: Job<SyncJobData>): Promise<void> {
 62	 const { codebaseId, type, metadata } = job.data;
 63	 const syncJobId = metadata?.syncJobId;
 64	
 65	 this.logger.log(`Starting parallel sync orchestration for codebase: ${codebaseId}`);
 66	
 67	 // Acquire distributed lock for this codebase
 68	 const lockResource = `sync-orchestration:${codebaseId}`;
 69	 const lockResult = await this.distributedLockService.acquireLock(lockResource, {
 70	 ttl: 3600000, // 1 hour
 71	 maxRetries: 3,
 72	 });
 73	
 74	 if (!lockResult.acquired) {
 75	 throw new Error(`Failed to acquire sync lock for codebase ${codebaseId}: ${lockResult.error}`);
 76	 }
 77	
 78	 try {
 79	 // Update job status to running
 80	 await this.updateSyncJob(syncJobId, {
 81	 status: SyncJobStatus.RUNNING,
 82	 startedAt: new Date(),
 83	 progress: 0,
 84	 });
 85	
 86	 // Emit WebSocket update
 87	 await this.emitSyncUpdate(syncJobId, codebaseId, {
 88	 status: 'RUNNING',
 89	 progress: 0,
 90	 message: 'Starting parallel sync orchestration',
 91	 });
 92	
 93	 // Execute parallel sync orchestration
 94	 await this.orchestrateParallelSync(codebaseId, type, syncJobId, metadata);
 95	
 96	 // Mark sync as completed
 97	 await this.updateSyncJob(syncJobId, {
 98	 status: SyncJobStatus.COMPLETED,
 99	 completedAt: new Date(),
 100	 progress: 100,
 101	 });
 102	
 103	 await this.emitSyncUpdate(syncJobId, codebaseId, {
 104	 status: 'COMPLETED',
 105	 progress: 100,
 106	 message: 'Parallel sync orchestration completed',
 107	 });
 108	
 109	 this.logger.log(`Completed parallel sync orchestration for codebase: ${codebaseId}`);
 110	 } finally {
 111	 // Release distributed lock
 112	 await this.distributedLockService.releaseLock(lockResource, lockResult.lockId!);
 113	 }
 114	 }
