 116	 /**
 117	 * Orchestrate parallel sync pipelines (following codebase-indexer pattern)
 118	 */
 119	 private async orchestrateParallelSync(
 120	 codebaseId: string,
 121	 type: SyncJobType,
 122	 syncJobId: string,
 123	 metadata: any,
 124	 ): Promise<void> {
 125	 this.logger.log(`Orchestrating parallel sync for codebase: ${codebaseId}, type: ${type}`);
 126	
 127	 try {
 128	 // Phase 1: Repository Sync & File Discovery
 129	 await this.updateProgress(codebaseId, syncJobId, 'repository-sync', 10, 'Starting repository sync and file discovery');
 130	 await this.executeRepositorySync(codebaseId, syncJobId, metadata);
 131	
 132	 // Phase 2: Get discovered files
 133	 await this.updateProgress(codebaseId, syncJobId, 'file-discovery', 20, 'Discovering files in repository');
 134	 const files = await this.getDiscoveredFiles(codebaseId);
 135	
 136	 if (files.length === 0) {
 137	 this.logger.warn(`No files discovered for codebase ${codebaseId}`);
 138	 await this.updateProgress(codebaseId, syncJobId, 'completed', 100, 'No files found to process');
 139	 return;
 140	 }
 141	
 142	 // Phase 3: Execute parallel processing pipelines (codebase-indexer approach)
 143	 await this.updateProgress(codebaseId, syncJobId, 'parallel-processing', 30, `Processing ${files.length} files in parallel pipelines`);
 144	 await this.executeParallelProcessingPipelines(codebaseId, files, syncJobId, type);
 145	
 146	 // Phase 4: Finalize and update codebase status
 147	 await this.updateProgress(codebaseId, syncJobId, 'finalizing', 90, 'Finalizing sync process');
 148	 await this.finalizeSyncProcess(codebaseId, syncJobId);
 149	
 150	 await this.updateProgress(codebaseId, syncJobId, 'completed', 100, 'Sync completed successfully');
 151	 } catch (error) {
 152	 this.logger.error(`Error in parallel sync orchestration for codebase ${codebaseId}:`, error);
 153	 await this.updateProgress(codebaseId, syncJobId, 'failed', 0, `Sync failed: ${error.message}`);
 154	 throw error;
 155	 }
 156	 }
