 139	 // Wait for all pipelines to complete
 140	 const results = await Promise.allSettled(promises);
 141	
 142	 // Check for failures
 143	 const failures = results.filter(result => result.status === 'rejected');
 144	 if (failures.length > 0) {
 145	 this.logger.error(`${failures.length} pipelines failed:`, failures);
 146	 // Don't throw - allow partial success
 147	 }
 148	
 149	 // Update progress
 150	 await this.updateJobProgress(jobId, 100, 'All pipelines completed');
 151	 }
