 57	 /**
 58	 * Main orchestrator - Coordinates parallel pipelines (codebase-indexer approach)
 59	 */
 60	 @Processor(JOB_TYPES.SYNC_CODEBASE)
 61	 async handleSyncCodebase(job: Job<SyncJobData>): Promise<void> {
 62	 const { codebaseId, type, metadata } = job.data;
 63	 const syncJobId = metadata?.syncJobId;
 64	
 65	 this.logger.log(`Starting parallel sync orchestration for codebase: ${codebaseId}`);
 66	
 67	 // Acquire distributed lock for this codebase
 68	 const lockResource = `sync-orchestration:${codebaseId}`;
 69	 const lockResult = await this.distributedLockService.acquireLock(lockResource, {
 70	 ttl: 3600000, // 1 hour
 71	 maxRetries: 3,
 72	 });
 73	
 74	 if (!lockResult.acquired) {
 75	 throw new Error(`Failed to acquire sync lock for codebase ${codebaseId}: ${lockResult.error}`);
 76	 }
...
 126	
 127	 try {
 128	 // Phase 1: Repository Sync & File Discovery
 129	 await this.updateProgress(codebaseId, syncJobId, 'repository-sync', 10, 'Starting repository sync and file discovery');
 130	 await this.executeRepositorySync(codebaseId, syncJobId, metadata);
 131	
 132	 // Phase 2: Get discovered files
 133	 await this.updateProgress(codebaseId, syncJobId, 'file-discovery', 20, 'Discovering files in repository');
 134	 const files = await this.getDiscoveredFiles(codebaseId);
 135	
 136	 if (files.length === 0) {
 137	 this.logger.warn(`No files discovered for codebase ${codebaseId}`);
 138	 await this.updateProgress(codebaseId, syncJobId, 'completed', 100, 'No files found to process');
 139	 return;
 140	 }
